if (not ItemQuestsPerDB.FilterCompleted) then ItemQuestsPerDB.FilterCompleted = false; end
QuestItems.Data.Faction, QuestItems.Data.LocalFaction = UnitFactionGroup("player")
QuestItems.Data.LocalRace, _, QuestItems.Data.Race = UnitRace("player")
QuestItems.Data.LocalClass, _, QuestItems.Data.Class = UnitClass("player")
QuestItems.Data.Race, QuestItems.Data.Class =  math.pow(2, (QuestItems.Data.Race - 1)), math.pow(2, (QuestItems.Data.Class - 1))
end
local function Filter_Hide(QuestList)
if (not QuestList) then
return nil
else
if (not ItemQuestsPerDB.FilterCompleted) then
return QuestList
else
local Quests, Valid = {}, false
for k, v in pairs(QuestList) do
if (not IsQuestFlaggedCompleted(k)) then
Quests[k] = v
Valid = true
end
end
if (Valid) then return Quests; else return nil; end
end
end
end
function QuestItems.Functions.ShowQuests(ItemID)
if (not ItemID) then return; end
local ItemQuests = Filter_Hide(QuestItems.Data.DB[ItemID])
if (not ItemQuests) then return; end
local QuestDisplay = ""
if (ItemQuestsPerDB.Filter == 1) then
local QuestList = {}
for k, v in pairs (ItemQuests) do QuestList[v[1]] = k end
for k, v in pairs(QuestList) do
if (QuestDisplay ~= "") then QuestDisplay = QuestDisplay..", "; end
QuestDisplay = QuestDisplay..k
end
elseif (ItemQuestsPerDB.Filter == 2) then
local QuestList, Races = {}
if (QuestItems.Data.Faction == "Alliance") then Races = {1, 3, 4, 7}; else Races = {2, 5, 6, 8}; end
for k, v in pairs (ItemQuests) do
local ValidList = {}
for kk, vv in pairs(Races) do
ValidList[vv] = true
if ((v[3] ~= 0) and (v[3] ~= 255)) then
if (bit.band(v[3], math.pow(2, vv-1)) == 0) then ValidList[vv] = false; end
end
end
for kk, vv in pairs(ValidList) do
if (vv) then
QuestList[v[1]] = true
break
end
end
end
for k, v in pairs(QuestList) do
if (QuestDisplay ~= "") then QuestDisplay = QuestDisplay..", "; end
QuestDisplay = QuestDisplay..k
end
elseif (ItemQuestsPerDB.Filter == 3) then
local QuestList = {}
for k, v in pairs (ItemQuests) do
local Valid = true
if (v[3]) and (v[3] ~= 0) and (v[3] ~= 255) then
if (bit.band(v[3], QuestItems.Data.Race) == 0) then Valid = false; end
end
if (Valid) then
if (v[2]) then
if (bit.band(v[2], QuestItems.Data.Class) == 0) then Valid = false; end
end
end
if (Valid) then QuestList[v[1]] = true; end
end
for k, v in pairs(QuestList) do
if (QuestDisplay ~= "") then QuestDisplay = QuestDisplay..", "; end
QuestDisplay = QuestDisplay..k
end
end
if (QuestDisplay == "") then return; end
GameTooltip:AddLine(" ")
GameTooltip:AddLine("Quest Item For:")
GameTooltip:AddLine(QuestDisplay, 0.80, 0.80, 1, 1)
end
function QuestItems.Functions.Tooltip(Self, msg)
Self:SetScript("OnEnter", function()
GameTooltip:SetOwner(Self, "ANCHOR_RIGHT")
GameTooltip:SetText(msg, nil, nil, nil, nil, true)
GameTooltip:Show()
end)
Self:SetScript("OnLeave", function() GameTooltip_Hide() end)
end
function QuestItems.Functions.FilterClick(ID)
if (not QuestItems.Frames.Options.Filter[ID]:GetChecked()) then
QuestItems.Frames.Options.Filter[ID]:SetChecked(true)
return
end
for x = 1, 3 do QuestItems.Frames.Options.Filter[x]:SetChecked(x == ID) end
ItemQuestsPerDB.Filter = ID
end
