function QuestItems.Functions.CreateEvents()
GameTooltip:HookScript("OnTooltipSetItem", function(Self, ...)
local Item = select(2, Self:GetItem())
if (Item and (type(Item) == "string")) then
local data = select(3, strfind(Item, "item:(%d+)"))
if (data) then QuestItems.Functions.ShowQuests(tonumber(data)); end
end
end)
