    [4243] = "Encuentra a Memo 01 en el Cráter de Un'Goro.",
    [4244] = "Encuentra una envoltura de mitril y reúnete de nuevo con Memo 01 en el Cráter de Un'Goro.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - 1 x Envoltura de mitril",
    [4245] = "Lleva a Memo 01 de vuelta con Karna Tripirrem al Cráter de Un'Goro.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Lleva a Memo 01 de vuelta con Karna Tripirrem",
    [4261] = "Escolta a Arei y luego habla con Kayneth Ventoleve que está en Canción del Bosque, en la parte este de Vallefresno.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Ayuda a Arei ponerse a salvo",
    [4265] = "Libera a Raschal de la vaina de los Zukk'ash.\n\nVuelve al Bastión Plumaluna con Ginro Mechogar e infórmale de que Raschal está bien.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Libera a Raschal",
    [4266] = "Habla con Shandris Plumaluna del Bastión Plumaluna.",
    [4267] = "Lleva el informe de Raschal a Gracina Poderespíritu a Darnassus.",
    [4281] = "Lleva el paquete sin entregar a Díscolo Falfindel a Thalanaar.",
    [4283] = "Reúne 50 medallones Roca Negra.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - 50 x Medallón Roca Negra",
