    [2933] = "Apporter une Bouteille de venin à un apothicaire, à Moulin-de-Tarren.",
    [2934] = "Rapporter une Glande à venin intacte à l'Apothicaire Lydon, à Moulin-de-Tarren.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Glande à venin intacte x 1",
    [2939] = "Parler à Daryn Calmevent, au village de Rut'theran.",
    [2940] = "Demander à Daryn Calmevent si vous pouvez emprunter son livre.",
    [2941] = "Apporter la lettre à Gruzouille Sautelevier, à Tanaris.",
    [2942] = "Rapporter la Pierre étincelante et le Bâton d'Equinex à Troyas Lunebise, dans le bastion de Pennelune.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Une pierre étincelante x 1",
    [2943] = "Apporter le livre à Troyas Lunebise, dans le bastion de Pennelune.",
    [2944] = "Utiliser le Preneur d'images FX pour prendre un cliché de Gammerita, puis retourner voir Daryn Calmevent à Rut'theran.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Photo de Gammerita x 1",
    [2969] = "À l'aide de la Clé de la cage en bambou, libérer les Fées fléchetteuses de l'enclos dans le Camp du Totem-sinistre. Au moins 6 Fées fléchetteuses et Kindal Tisselune doivent survivre.\n\nVous avez 6 minutes avant que Kindal décide qu'il est temps de battre en retraite et que vous échouiez dans votre quête.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Libérer au moins 6 Fées fléchetteuses",
    [2970] = "Tuer 12 Naturalistes du Totem-sinistre, 10 Écumeurs du Totem-sinistre et 6 Chamans du Totem-sinistre avant de retourner voir Jer'kai Tisselune, en Féralas.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Naturaliste du Totem-sinistre x 12\n - Ecumeur du Totem-sinistre x 10\n - Chaman du Totem-sinistre x 6",
    [2972] = "Apporter la Chevalière de Jer'kai à la Grande prêtresse Tyrande Murmevent, à Darnassus.",
    [2973] = "Rapporter 10 Ailes iridescentes de Fées fléchetteuses à Krueg Casse-crâne au Camp Mojache.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Aile iridescente de fée fléchetteuse x 10",
    [2974] = "Apporter 20 Cornes de Totem-sinistre à Krueg Casse-crâne au Camp Mojache.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Corne de Totem-sinistre x 20",
    [2975] = "Tuer 10 Ogres Gordunni, 10 Ogre-magi Gordunni et 5 Brutes Gordunni pour Rok Orhan, du Camp Mojache.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Ogre Gordunni x 10\n - Ogre-mage Gordunni x 10\n - Brute Gordunni x 5",
    [2976] = "Apportez la Caisse de Cornes de Totem-sinistre à Belgrom Cogneroche, Orgrimmar.",
    [2978] = "Apportez le Parchemin gordunni à Rok Orhan, au Camp Mojache.",
    [2979] = "Rok Orhan, du Camp Mojache, veut que vous trouviez un Orbe Gordunni.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Orbe gordunni x 1",
    [2980] = "Tuer 10 Chamans Gordunni, 10 Démonistes Gordunni et 5 Marteleurs Gordunni pour Rok Orhan, du Camp Mojache.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Chaman Gordunni x 10\n - Démoniste Gordunni x 10\n - Marteleur Gordunni x 5",
    [2982] = "Tuer 8 Démonistes Gordunni, 8 Chamans Gordunni et 8 Brutes Gordunni pour Angelas Lunebise.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Démoniste Gordunni x 8\n - Chaman Gordunni x 8\n - Brute Gordunni x 8",
    [2987] = "Collecter 12 échantillons de Cobalt Gordunni pour Orwin Azztuche, du Camp Mojache.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Cobalt gordunni x 12",
    [2988] = "Inspecter les cages dans les deux villages Fânécorce, puis retourner auprès du Maître des griffons Hachegriffe.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Inspecter la première cage x 1\n - Inspecter la seconde cage x 1\n - Inspecter la troisième cage x 1",
    [2989] = "Regarder sur le dessus de l'autel de Zul, puis retourner auprès du Maître des griffons Hachegriffe.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Fouiller l'autel de Zul",
    [2990] = "Apportez la Plume de Bec-tranchant à Thadius Sinissombre, dans les Terres foudroyées.",
    [2995] = "Oran Serpenroule, de Fossoyeuse, vous demande de vous rendre dans les Hinterlands et d'y brûler les Registres du Haut-val, le Rapport du Haut-val et les Notes du Haut-val.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Brûler les Registres du Haut-val x 1\n - Brûler les Notes du Haut-val x 1\n - Brûler le Rapport du Haut-val x 1",
    [2997] = "Parlez à Tiza Forgeguerre, à Forgefer.",
    [3002] = "Apportez l'Orbe Gordunni à Uthel'nay, au pavillon de Buissombre, à Orgrimmar.",
    [3022] = "Apportez la Caisse soigneusement empaquetée à Erelas Ambreciel, au village de Rut'theran.",
    [3062] = "Rapporter le Cœur noir d'Edana Serrehaine à Talo Sabot-de-ronce, au Camp Mojache.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Coeur noir d'Edana x 1",
    [3063] = "Tuer 4 Harpies Ruissenord, 4 Volplumes Ruissenord, 4 Imploratrices célestes Ruissenord et 4 Pourfendeuses Ruissenord pour Talo Sabot-de-ronce.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Harpie Ruissenord x 4\n - Volplume Ruissenord x 4\n - Pourfendeuse Ruissenord x 4\n - Imploratrice céleste Ruissenord x 4",
    [3113] = "Lire la Note de service codée, et parler à Solm Hargrin, dans la Vallée des Frigères.",
    [3121] = "Porter la Tête réduite à Xerash Lamefeu, au Pavillon de Lariss, en Féralas.",
    [3122] = "Livrer une Poche d'herbes de Xerash au Féticheur Uzer'i, en Féralas.",
    [3123] = "Tuez 10 Ours Ferpoil ou Coureurs Longues-dents, puis utilisez le Récipient de muisek pour faire rétrécir et capturer les bêtes vaincues.\n\nRamenez 10 Bêtes miniaturisées et le Récipient de Bêtes miniaturisées au Féticheur Uzer'i, en Féralas.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Bête miniaturisée x 10",
    [3124] = "Tuer 10 Hippogriffes Aigreplume de n'importe quel type, puis utiliser le Vaisseau de muisek pour les faire rétrécir et les capturer.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Hippogriffe miniaturisé x 10",
    [3125] = "Tuer 8 Fées fléchetteuses ou Petits dragons féeriques. Utiliser le Vaisseau de muisek pour faire rétrécir et capturer les dragons féeriques vaincus.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Dragon féerique miniaturisé x 8",
    [3126] = "Tuer 3 Marcheurs des bois errants. Utiliser le Vaisseau de muisek pour faire rétrécir et pour capturer les tréants vaincus.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Tréant miniaturisé x 3",
    [3127] = "Tuer 7 Marcheurs des terres ou Géants des falaises. Utiliser le Récipient de muisek pour faire rétrécir et pour capturer les Géants des montagnes.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Géant des montagnes miniaturisé x 7",
    [3128] = "Apporter 2 Bûches fendues, 6 Minerais incrustés, 20 Morceaux de tendons résistants et 40 Fragments métalliques au Féticheur Uzer'i au Camp Mojache.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Bûches fendues x 2\n - Minerais incrustés x 6\n - Tendon résistant x 20\n - Fragments métalliques x 40",
    [3129] = "Le Féticheur Uzer'i veut que vous patientez pendant qu'il prépare les armes.",
    [3130] = "Se présenter devant Latronicus Lancelune au bastion de Pennelune.",
    [3161] = "Marvon Chercherivet, à Tanaris, vous a demandé de ramasser 30 Ornements en gahz'ridienne.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Ornement en gahz'ridienne x 30",
    [3181] = "Porter la Corne Gigantesque de Margol à la Montagnarde Minigravillon.",
    [3182] = "Porter la Corne gigantesque de Margol au conservateur en chef Thorius à Forgefer.",
    [3201] = "Apporter le document de preuve à la Montagnarde Minigravillon.",
    [3362] = "Tran'rek, à Gadgetzan, voudrait que vous tuiez 8 Chardonniers noueux et 8 Chardonniers taille-racines.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Chardonnier noueux x 8\n - Chardonnier taille-racine x 8",
    [3367] = "Aider Dorius à retourner à Forgefer.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - L'escorte de Dorius",
    [3368] = "Apporter la Lettre roussie à Thorius à Forgefer. Il se trouve dans le Hall des Explorateurs.",
    [3371] = "Retourner à la Gorge des Vents brûlants et trouver l'Equipe d'archéologues de Dorius.",
    [3372] = "Trouver l'Artefact mystérieux gardé par les Idolâtres du Marteau du crépuscule dans le quart nord-est de la Gorge des Vents brûlants et ramenez-le à l'Autel de Suntara dans la Fosse aux scories.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Artéfact mystérieux x 1",
    [3377] = "Écouter le récit de Zamael.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Histoire de Zamael",
    [3378] = "Trouver la Prière à Elune et l'apporter à Astarii Cherchétoile à Darnassus.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Prière à Elune x 1",
    [3380] = "Trouver Marvon Chercherivet à Tanaris.",
    [3381] = "Apporter 2 Plumes d'hippogriffe sans défaut à Sanath Lim-yo, en Azshara, si vous souhaitez parler à son maître.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Plume d'hippogriffe sans défaut x 2",
    [3441] = "Écouter le récit de Velarok Lamevent.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Histoire de Velarok",
    [3442] = "Réunir 4 Cœurs des flammes et 4 globes d’Huile de golem puis rapporter ces objets à Velarok Lamevent.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Cœur des flammes x 4\n - Huile de golem x 4",
    [3443] = "Apporter 8 Dagues plaquées en thorium à Velarok Lamevent.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Dague plaquée en thorium x 8",
    [3444] = "Récupérer le Cercle de pierres à l'Atelier de Marvon Chercherivet à Cabestan.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Cercle de pierres x 1",
    [3445] = "Trouver Marvon Chercherivet à Tanaris.",
    [3448] = "Parlez au mage Tymor à Forgefer.",
    [3449] = "À l'aide de la Trousse de dessin, effectuer des reproductions de la Rune de Beth'Amara, de la Rune de Jin'yael, de la Rune de Markri, de la Rune de Sael'hai, puis se diriger vers la petite île près de la Péninsule méridionale d'Azshara et signaler au Pilote Xiggs Allumèche de venir les prendre.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Reproduction : Rune de Beth'Amara x 1\n - Reproduction : Rune de Jin'yael x 1\n - Reproduction : Rune de Markri x 1\n - Reproduction : Rune de Sael'hai x 1",
    [3450] = "Parlez à Xiggs Allumèche à Forgefer.",
    [3451] = "Parlez à Xiggs Allumèche à Forgefer pour obtenir un Lance-fusée standard.",
    [3452] = "Trouver et apporter un Symbole de Ragnaros à Velarok Lamevent.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Symbole de Ragnaros x 1",
    [3453] = "Attendre que Velarok Lamevent termine la Torche de vindicte.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Création de torche",
    [3454] = "Prendre la Torche de vindicte.",
    [3461] = "Rapporter la Trousse de dessin du mage Tymor au mage Tymor, à Forgefer.",
    [3462] = "Parler à l'écuyer Maltrake.",
    [3463] = "Mettre le feu aux Tours de garde nord, sud, est et ouest en utilisant la Torche de vindicte à l'intérieur de chaque tour.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Tour ouest en feu x 1\n - Tour sud en feu x 1\n - Tour est en feu x 1\n - Tour nord en feu x 1",
    [3481] = "Ouvrir le coffre.",
    [3501] = "Apporter des Fragments imparfaits de draenéthyste à Kum'isha et toucher une récompense pour chaque fragment rapporté.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Fragment de draenéthyste imparfait x 1",
    [3504] = "Apporter la Lettre scellée à Ag'tor Poing-de-sang en Azshara.",
    [3505] = "Tuer 10 Vengeurs elfes de sang et 10 Géomètres elfes de sang avant de rechercher le campement du magus Rimtori. Une fois le camp trouvé, chercher quelque chose d'important pour elle et qui peut être utilisé pour l'obliger à se battre.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Vengeur elfe de sang x 10\n - Géomètre elfe de sang x 10\n - Trouver le camp du magus Rimtori",
    [3506] = "Détruire le Cristal de focalisation des arcanes lié au magus Rimtori et l'assassiner ensuite. Puis apporter la tête du magus Rimtori à Ag'tor Poing-de-sang en Azshara.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Tête du magus Rimtori x 1",
    [3507] = "Aller voir Belgrom Cogneroche à Orgrimmar.",
    [3517] = "Trouver la Tablette de Beth'Amara, la Tablette de Jin'yael, la Tablette de Markri et la Tablette de Sael'hai puis retourner voir Jediga en Azshara.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Tablette de Beth'Amara x 1\n - Tablette de Jin'yael x 1\n - Tablette de Markri x 1\n - Tablette de Sael'hai x 1",
    [3518] = "Apportez la Tablette de Beth'Amara à Magatha aux Pitons du Tonnerre.",
    [3520] = "Capturer l'Esprit de 3 Hurleurs en Féralas, puis retourner voir Yeh'kinya à Port Gentepression.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Collecte d'Esprits de Hurleurs x 3",
    [3541] = "Apportez la Tablette de Jin'yael à Jes'rimon à Orgrimmar.",
    [3542] = "Apportez la Tablette de Markri à Andron Gant à Fossoyeuse.",
    [3561] = "Apportez la Tablette de Sael'hai à l'archimage Xylem en Azshara.",
    [3562] = "Apporter la Note de Magatha à Jediga en Azshara.",
    [3563] = "Apporter la Note de Jes'rimon à Jediga en Azshara.",
    [3564] = "Apporter la Note d'Andron à Jediga en Azshara.",
    [3565] = "Apporter la Note de Xylem à Jediga en Azshara.",
    [3566] = "Assassiner Lathoric le Noir et Obsidion, puis retourner voir Thorius, à Forgefer, avec la Tête de Lathoric le Noir et le Cœur d'Obsidion.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Tête de Lathoric le Noir x 1\n - Cœur d'Obsidion x 1",
    [3568] = "Avant de retourner voir le Chimiste Cuely, remplir les 4 Fioles vides dans les cuvettes de marées le long de la côte des Ruines d'Eldarath, en Azshara.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Fiole pleine marquée N°1 x 1\n - Fiole pleine marquée N°2 x 1\n - Fiole pleine marquée N°3 x 1\n - Fiole pleine marquée N°4 x 1",
    [3569] = "Donner l'élixir de Cuely à Thersa Chant-du-vent, à Fossoyeuse.",
    [3601] = "Fouiller le Camp de base thalassien pour trouver la Boussole, la Lunette, le Poulet farci et le Wizzlegoober de Kim'Jael, puis les lui rapporter en Azshara.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Boussole de Kim'Jael x 1\n - Lunette de Kim'Jael x 1\n - Poulet farci de Kim'Jael x 1\n - Wizzlegoober de Kim'Jael x 1",
    [3661] = "Collecter 15 Plumes d'Indomptable dans les Hinterlands, pour Erelas Ambreciel au village de Rut'theran.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Plume d'Indomptable x 15",
    [3701] = "Aller dans les ruines de Thaurissan, dans les Steppes ardentes. Récupérer des informations sur les Reliques de Thaurissan, puis retourner auprès de l'Historienne royale Archesonus.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Information récupérée x 12",
    [3702] = "Écouter l'historienne royale Archesonus raconter l'histoire de Thaurissan.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Histoire de Thaurissan",
    [3761] = "Apporter 20 Échantillons d'humus d'Un'Goro à Ghede, aux Pitons du Tonnerre.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Humus d'Un'Goro x 20",
    [3764] = "Apporter 20 échantillons d'Humus d'Un'Goro à Jenal dans l'Enclave cénarienne à Darnassus.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Humus d'Un'Goro x 20",
    [3781] = "Apporter le Bon pour des graines de l'archidruide à Mathrengyl Pasdelours dans l'Enclave cénarienne à Darnassus.",
    [3782] = "Apporter le Bon pour des graines à Bashana Totem-runique aux Pitons du Tonnerre.",
    [3783] = "Récupérez 10 Fourrures épaisses de yéti pour Umi Sarcafroisse, à Long-guet.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Fourrure épaisse de yéti x 10",
    [3785] = "Utiliser une Sacoche de printemps avec une pochette de graines de Tharlendis et deux échantillons d'Humus d'Un'Goro pour essayer de cultiver des Fibres d'aurore.\n\nApporter 10 Fibres d'aurore à Mathrengyl Pasdelours à Darnassus.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Fibre d'aurore x 10",
    [3790] = "Parler à l'archidruide Fandral Forteramure à Darnassus.",
    [3821] = "S'aventurer dans les Steppes ardentes et découvrir ce qu'il est advenu de Sha'ni Fière-défense.",
    [3822] = "Récupérer l'Anneau de nez de Sha'ni sur Krom'Grul, et le rapporter à Thal'trak, à Kargath. Fouiller les différentes grottes et mines du Rocher des Cognepeurs.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Anneau de nez de Sha'ni x 1",
    [3823] = "Tuer 15 Ogres-mages Brûlentrailles, 7 Ogres Brûlentrailles et 7 Brutes Brûlentrailles.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Ogre-mage Brûlentrailles x 15\n - Ogre Brûlentrailles x 7\n - Brute Brûlentrailles x 7",
    [3824] = "Trouver la Tête coupée de Gor'tesh.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Tête coupée de Gor'tesh x 1",
    [3825] = "Prendre la Tête coupée de Gor'tesh et la placer au sommet du Rocher des Cognepeurs. Chercher un monticule de terre meuble pour planter la pique.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - La tête de Gor'tesh à son sommet x 1",
    [3841] = "Parler à Quentin aux Mille pointes.",
    [3842] = "Apporter 2 Elixirs de Robustesse à Quentin, aux Mille pointes.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Elixir de robustesse x 2",
    [3843] = "Emmenez votre Œuf fragile de Fée fléchetteuse dans les Hinterlands et parlez à Agnar Domptebêtes. Dépêchez-vous, vous n'avez qu'une heure pour le trouver avant que le petit ne meure dans l'oeuf.",
    [3844] = "Rechercher dans l'étang un indice sur l'identité du propriétaire du radeau brisé.",
    [3845] = "Examiner les objets du paquet et les apporter à leur propriétaire.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Grande boussole x 1\n - Carte enroulée x 1\n - Clé à tête de lion x 1",
    [3881] = "Apporter une Caisse de nourriture et de l'Equipement de recherche à Williden Marshal dans le Cratère d'Un'Goro.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Caisse de nourriture x 1\n - Equipement de recherche x 1",
    [3882] = "Apporter 8 Os de dinosaure à Étincelle Minerien, au cratère d'Un'Goro.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Os de dinosaure x 8",
    [3883] = "Utiliser la Fiole de raclement pour récolter un Échantillon de paroi de la ruche dans une des nurseries de la ruche Gorishi, à l'intérieur du cratère d'Un'Goro. Chercher les chambres qui contiennent des larves suspendues.\n\nApporter l'Échantillon de paroi de la ruche à Hol'anyee Marshal dans le cratère d'Un'Goro.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Echantillon de paroi de la ruche x 1",
    [3884] = "Rapporter le Journal à Williden Marshal quelque part dans le Cratère d'Un'Goro.",
    [3908] = "Apporter l'Epée de Linken à Donova Antreneige au Berceau-de-l'Hiver.",
    [4022] = "Montrer à Cyrus Therepentous le Cuir de dragon noir que vous avez reçu de Velarok Lamevent.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Cuir de dragon noir x 1\n - Preuve présentée",
    [4061] = "S'aventurer dans les Steppes ardentes et récupérer 10 Fragments d'élémentaires cassés pour la Hiérophante Theodora Mulvadania.\n\nTheodora vous a indiqué que les golems et les élémentaires étaient la source de ces fragments.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Fragment d'élémentaire cassé x 10",
    [4101] = "Apporter 15 Ambres de sang à Arathandris Argeciel à Gangrebois.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Ambre de sang x 15",
    [4102] = "Apporter 15 Ambres de sang à Maybess Brise-des-rivières à Gangrebois.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Ambre de sang x 15",
    [4103] = "|Cffffd100Conditions :|r\n - Fragment d'âme corrompue x 6",
    [4120] = "Talo Sabot-de-ronce, du Camp Mojache en Féralas, voudrait que vous tuiez 12 Grizzlys de la griffe colérique et 12 Ravageurs Gangrepatte, à Gangrebois.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Grizzly de la griffe colérique x 12\n - Ravageur Gangrepatte x 12",
    [4124] = "Parler à Ginro Attisâtre dans le bastion de Pennelune.",
    [4125] = "Localiser le bateau du messager quelque part le long de la côte de Féralas.",
    [4127] = "Retourner au bastion de Pennelune et montrer à Ginro Attisâtre le Couteau perdu que vous avez découvert.",
    [4129] = "Apporter le Couteau perdu à Quintis Gentecime au bastion de Pennelune.",
    [4130] = "Parler à Ginro Attisâtre dans le bastion de Pennelune.",
    [4131] = "Fouiller les Camps des gnolls Griffebois dans la partie sud de Féralas pour trouver des indices sur les possessions du messager ou sur le messager lui-même.",
    [4133] = "Parler à la maîtresse des ombres Vivian Lagrave à Kargath.",
    [4135] = "À présent que la dernière destination connue de Raschal est découverte, continuez à le rechercher, lui ou ses restes, dans le Gouffre grouillant. D'après le message, il se trouve au sud des camps des gnolls Griffebois.",
