    [4223] = "Parler au maréchal Maxwell dans les Steppes ardentes.",
    [4224] = "Parlez à John le Loqueteux.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - L'histoire de John le Loqueteux",
    [4243] = "Trouver A-mi 01 dans le Cratère d'Un'Goro.",
    [4244] = "Trouver une Armature en mithril et retourner auprès d'A-mi 01 dans le Cratère d'Un'Goro.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Armature en mithril x 1",
    [4245] = "Escorter A-mi 01 sur le chemin du retour jusqu'à Karna Songerrance dans le Cratère d'Un'Goro.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Protéger A-mi 01 jusqu'à ce que vous atteigniez Karna Songerrance",
    [4261] = "Escorter Arei jusqu'à un lieu sûr, puis parler à Kayneth Mortebrise au Chant des forêts, dans l'est d'Orneval.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Aider Arei à se rendre en un lieu sûr",
    [4265] = "Libérer Raschal de la ruche de Zukk'ash.\n\nRevenir voir Ginro Attisâtre au bastion de Pennelune et lui dire que Raschal est sain et sauf.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Libérer Raschal",
    [4266] = "Parler à Shandris Pennelune dans le bastion de Pennelune.",
    [4267] = "Apporter le Rapport de Raschal à Gracina Forcesprit à Darnassus.",
    [4281] = "Apportez le Vieux colis à Falfindel Gardevoie, à Thalanaar.",
    [4283] = "Récupérez 50 Médaillons de Rochenoire.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Médaillon de Rochenoire x 50",
    [4284] = "Récupérer 7 Cristaux de puissance de chaque couleur (rouge, bleu, jaune et vert). Les apporter à J.D. Collie au refuge des Marshal.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Cristal de puissance rouge x 7\n - Cristal de puissance jaune x 7\n - Cristal de puissance vert x 7\n - Cristal de puissance bleu x 7",
    [4285] = "Découvrir l'emplacement du Pylône de cristal nord dans le Cratère d'Un'Goro.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Découvrir et examiner le Pylône de cristal nord",
    [4287] = "Trouver l'emplacement du Pylône de cristal est dans le Cratère d'Un'Goro.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Trouver et examiner le Pylône de cristal est",
    [4288] = "Découvrir l'emplacement du Pylône de cristal ouest.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Trouver et examiner le Pylône de cristal ouest",
    [4289] = "Apporter 2 Peaux de gorille d'Un'Goro, 2 Peaux de marteleur d'Un'Goro et 2 Peaux de foudroyeur d'Un'Goro à Torwa le Guide à l'entrée du cratère d'Un'Goro.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Peau de gorille d'Un'Goro x 2\n - Peau de Marteleur d'Un'Goro x 2\n - Peau de Foudroyeur d'Un'Goro x 2",
    [4290] = "Trouver la dernière victime de Lar'korwi et voler un Morceau de carcasse d'un battrodon, puis retourner voir Torwa le Guide au Cratère d'Un'Goro.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Morceau de carcasse d'un battrodon x 1",
    [4291] = "Apporter 2 Glandes de phéromone de ravasaure à Torwa le Guide près de l'entrée du Cratère d'Un'Goro.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Glande de phéromone de ravasaure x 2",
    [4292] = "En utilisant le contenu de la Sacoche de Torwa, appeler Lar'korwi et le vaincre. Puis apporter la Tête de Lar'korwi à Torwa le Guide près de l'entrée du Cratère d'Un'Goro.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Tête de Lar'korwi x 1",
    [4293] = "Ramassez des Échantillons de gelée de Gangrebois sur n'importe quel type de gelée de Gangrebois. Puis, en utilisant le Matériel d'expérience qui se trouve à côté du chimiste Fuely à Fossoyeuse, décelez les échantillons le plus corrompus. Apportez 5 Échantillons corrompus de Gangrebois que vous aurez obtenus au Chimiste Fuely.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Echantillon corrompu de Gangrebois x 5",
    [4294] = "Ramassez des Échantillons de gelée d'Un'Goro sur n'importe quel type de gelée du Cratère d'Un'Goro. Puis, en utilisant le Matériel d'expérience qui se trouve à côté du chimiste Fuely à Fossoyeuse, décelez les échantillons les plus purs. Apporter 5 Échantillons purs d'Un'Goro que vous aurez obtenus au Chimiste Fuely.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Echantillon pur d'Un'Goro x 5",
    [4296] = "Trouvez la Transcription de la tablette.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Transcription de la tablette x 1",
    [4297] = "Apporter 5 Flanchets de traqueur Crins-d'argent à Agnar Domptebêtes dans les Hinterlands.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Flanchet de traqueur Crins-d'argent x 5",
    [4298] = "Parler à Agnar Domptebêtes dans les Hinterlands.",
    [4300] = "Apporter 8 Griffes blanches de ravasaure à Jes'rimon à Orgrimmar.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Griffe blanche de ravasaure x 8",
    [4301] = "Apportez la Peau d'U'cha à Torwa le Guide à l'entrée du Cratère d'Un'Goro.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Peau d'U'cha x 1",
    [4321] = "Parler à J.D. Collie dans un moment.",
    [4324] = "Parler à Yuka Fermevanne dans les Steppes ardentes.",
    [4421] = "Tuer 11 Gangreliges Jadefeu, 9 Traqueurs des ombres Jadefeu, 9 Voleurs Jadefeu et Xavathras. Retourner voir Eridan Ventebleu, à Gangrebois, quand la mission est terminée.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Gangrelige Jadefeu x 11\n - Traqueur des ombres Jadefeu x 9\n - Voleur Jadefeu x 9\n - Xavathras x 1",
    [4441] = "Aller à Darnassus et utiliser la Fiole d'Eridan dans le Temple de la Lune, pour obtenir une Fiole d'eau bénite. Retourner voir Eridan avec la Fiole pleine.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Fiole d'eau bénite x 1",
    [4442] = "Attendre un moment avant de reparler à Eridan.",
    [4449] = "Tuer 8 Géologues Sombrefer et rapporter 15 morceaux d'Etoffe de soie à la personne enfermée dans les toilettes de la gorge des Vents brûlants.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Géologue Sombrefer x 8\n - Etoffe de soie x 15",
    [4450] = "Prendre la copie du Livre de Bonnacier, puis collecter les objets qui y sont listés avant d'aller voir Froisse Bonnacier, à Tanaris.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Livre de Bonnacier x 1\n - Patte cristalline solide x 20\n - Paquet en retard de livraison x 1\n - Flamberge de Bonnacier x 1",
    [4451] = "Aller dans la gorge des Vents brûlants et trouver une utilisation à la Clé des toilettes de Crasseboue que vous avez trouvée.",
    [4486] = "Parler à Duthorian Rall, à Hurlevent.",
    [4490] = "Parlez à Strahad Farsan, à Cabestan pour apprendre à invoquer un Palefroi corrompu.",
    [4491] = "Conduire Ringo auprès de Spraggle Frock, au refuge des Marshal.\n\nSi Ringo s'évanouit, utiliser la Gourde de Spraggle pour le réanimer.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Escorter Ringo jusqu'à Spraggle Frock au refuge des Marshal",
    [4492] = "Trouver Ringo sur la crête de la Fournaise.",
    [4493] = "Parler à l'Alchimiste Pilonzugg à Gadgetzan.",
    [4494] = "Parler à l'Alchimiste Pilonzugg à Gadgetzan.",
    [4496] = "Apporter une Glande olfactive Gorishi et 5 Échantillons d'humus d'Un'Goro à l'Alchimiste Pilonzugg, à Gadgetzan.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Glande olfactive gorishi x 1\n - Humus d'Un'Goro x 5",
    [4501] = "Tuer 10 Pterreurdactyles frénétiques, puis parler à Spraggle Frock, au refuge des Marshal.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Pterreurdactyle frénétique x 10",
    [4502] = "Collecter 9 échantillons de Cendres d'Un'Goro sur les Élémentaires de feu autour du volcan du Cratère d'Un'Goro, puis les apporter à Liv Rafistolier, à Cabestan.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Cendres d'Un'Goro x 9",
    [4503] = "Apporter 8 Écailles étoilées de dimeurtrodon et 8 Écailles étoilées de pterreurdactyles à Shizzle, au refuge des Marshal.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Ecaille étoilée de dimeurtrodon x 8\n - Ecaille étoilée de pterreurdactyle x 8",
    [4504] = "Collecter 12 échantillons de Goudron ultra collant pour Tran'rek, à Gadgetzan.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Goudron ultra collant x 12",
    [4505] = "Collecter un échantillon d'eau corrompue dans le puits de lune des satyres Jadefeu, puis le rapporter à Winna Hazzard, au Poste de la Vénéneuse.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Flacon rempli x 1",
    [4506] = "Apportez le chaton de Winna au puits de lune corrompu, relâchez-le, puis rapportez-le à Winna.\n\nUne fois de retour auprès de Winna, cliquez sur le chat pour le lui rendre.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Rapporter le Chat corrompu à Winna Hazzard",
    [4512] = "Apporter 6 Pots de Limons maudits remplis et 6 Pots de Limons corrompus remplis à Laris Bringuefourbi, à Forgefer.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Pot de Limon maudit rempli x 6\n - Pot de Limon corrompu rempli x 6",
    [4513] = "Apporter 10 Pots remplis d'échantillon pur à Laris Bringuefourbi, à Forgefer.\n\nSi vous éprouvez des difficultés à les trouver, allez voir Laris pour obtenir plus d'indices.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Pot d'échantillon pur plein x 10",
    [4521] = "Trull Faillemort de Gangrebois veut que vous tuiez 15 Chouettards déchaînés et 15 Chouettards éreintés.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Chouettard déchaîné x 15\n - Chouettard éreinté x 15",
    [4642] = "Apporter un Échantillon de Limon mélangé au Chimiste Fuely à Fossoyeuse.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Echantillon de gelée mélangé x 1",
    [4721] = "Trull Faillemort à Gangrebois veut que vous chassiez 10 Chouettards berserk au Berceau-de-l'Hiver.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Chouettard berserk x 10",
    [4726] = "Apporter 8 Essences de Rejeton et le Draco-Incarcinatrix 900 à Brikolette Toutevapeur à la Corniche des flammes dans les Steppes ardentes.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Essence de rejeton x 8",
    [4738] = "Parler à Menara Taillevide dans les Tarides.",
    [4741] = "Trull Faillemort à Gangrebois veut que vous chassiez 13 Chouettards lunaires.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Chouettard lunaire x 13",
    [4781] = "Apporter une Barre d'or à Xizk Beaupiqué dans la vallée de Strangleronce.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Barre d'or x 1",
    [4782] = "Retourner voir Menara Taillevide dans les Tarides.",
    [4783] = "Apporter 10 Fioles de sang de Fielfurie et 1 Pierre infernale inférieure à Menara Taillevide dans les Tarides.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Fiole de sang de Fielfurie x 10\n - Pierre d'Infernal inférieure x 1",
    [4784] = "Apportez du Fil d'or fin, 2 Charbons ardents et un Fragment d'âme à Menara Taillevide dans les Tarides.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Fil d'or fin x 1\n - Charbon ardent x 2\n - Fragment d'âme x 1",
    [4786] = "Attendre que Menara Taillevide ait terminé votre robe puis lui parler de nouveau.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Attendre que Menara Taillevide ait terminé votre objet",
    [4808] = "Donner la Lettre de Brikolette à Felnok Ressordacier à Long-guet, à Berceau-de-l'Hiver.",
    [4809] = "Collectez 8 Cornes intactes de Noroît.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Corne intacte de Noroît x 8",
    [4810] = "Apporter le Paquet de Felnok à Brikolette Toutevapeur à la Corniche des flammes, dans les Steppes ardentes.",
    [4842] = "Suivre les instructions de Donova Antreneige, puis faire un rapport.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Trouver la gorge du Sombre murmure",
    [4861] = "Localiser le camp démoli et les fournitures de Jaron Sculptepierre, au sud du Berceau-de-l'Hiver.",
    [4863] = "Trouver le chariot saccagé de Jaron Sculptepierre.",
    [4864] = "Ramasser les Fournitures de Jaron dans la neige, puis trouver l'Indomptable qui a volé l'amulette dont Jaron a parlé.\n\nRapporter les fournitures de Jaron et l'Amulette de la plume bleue à Jaron Sculptepierre dans son pavillon au Berceau-de-l'Hiver.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Fournitures de Jaron x 1\n - Amulette de la plume bleue x 1",
    [4882] = "Apporter le Collier de plumes bleues à Trull Faillemort au Poste de la Vénéneuse.",
    [4883] = "Apporter le Collier de plumes bleues à Nara Crin-sauvage.",
    [4901] = "Protéger Ranshalla pendant qu'elle s'efforce de réactiver l'Autel d'Elune. Faire un rapport de vos découvertes à Erelas Ambreciel, au village de Rut'theran.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Découvrir le secret de l'Autel d'Elune",
    [4902] = "Parler à l'archidruide Fandral Forteramure, à Darnassus.",
    [4906] = "Eridan Ventebleu de Gangrebois veut que vous tuiez 8 Implorateurs de l'enfer Jadefeu, 8 Traîtres Jadefeu, 8 Entourloupeurs Jadefeu et Xavaric. Revenez auprès d'Eridan une fois la tâche accomplie.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Implorateur de l'enfer Jadefeu x 8\n - Traître Jadefeu x 8\n - Entourloupeur Jadefeu x 8\n - Xavaric x 1",
    [4961] = "Tuer le Démon de l'Orbe, puis parler à Tabetha.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Démon de l'Orbe x 1",
    [4962] = "Prendre le Rubis de Felhas et l'utiliser sur l'un des gangrechiens de Désolace. Quand vous aurez réussi, rapporter le Rubis de Felhas et l'Esprit de gangrechien emprisonné à Menara Taillevide, dans les Tarides.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Esprit de gangrechien emprisonné x 1",
    [4963] = "Prendre l'Emeraude d'Infus et l'utiliser sur l'un des Infernaux qui se trouvent en Désolace. Quand vous aurez réussi, rapporter l'Emeraude d'Infus et l'Esprit d'Infernal emprisonné à Menara Taillevide, dans les Tarides.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Esprit infernal emprisonné x 1",
    [4964] = "Attendre que Menara Taillevide termine l'Orbe de Dar'Orahil, puis lui reparler.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Attendre que Menara Taillevide ait terminé votre objet",
    [4965] = "Parlez à Menara Taillevide, dans les Tarides.",
    [4969] = "Parlez à Menara Taillevide, dans les Tarides.",
    [4970] = "Collecter 5 morceaux de Viande de Croc acéré et 5 morceaux de Viande de Noroît pour Rivern Givrevent du Berceau-de-l'Hiver.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Viande de Croc acéré x 5\n - Viande de Noroît x 5",
    [4971] = "Utiliser le Déplaceur temporel à côté de l'un des silos d'Andorhal pour révéler des Parasites temporels.\n\nTuer 10 Parasites temporels et rapporter le Déplaceur temporel à Chromie, à l'auberge d'Andorhal, dans les Maleterres de l'ouest.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Parasite temporel x 10",
    [4972] = "Trouver 5 Montres d'Andorhal, dans des coffrets qui se trouvent parmi les décombres de la ville, et les rapporter à Chromie à l'Auberge d'Andorhal, dans les Maleterres de l'ouest.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Montre d'Andorhal x 5",
    [4976] = "Rapporter l'Orbe infernal purifié à Menara Taillevide, dans les Tarides.",
    [4984] = "Détruire 8 Loups malades, puis retourner voir Mulgris Rivière-profonde, au Repaire putride dans les Maleterres de l'ouest.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Loup malade x 8",
    [4985] = "Détruire 8 Grizzlys malades, puis retourner voir Mulgris Rivière-profonde au Repaire putride, dans les Maleterres de l'ouest.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Grizzly malade x 8",
    [4986] = "D'après la magie contenue dans la Branche de chêne à glyphes, le druide tauren voulait qu'elle soit apportée au Cercle cénarien, à Darnassus. Aller solliciter l'assistance de l'un des druides, là-bas.",
    [4987] = "D'après la magie contenue dans la Branche de chêne à glyphes, le druide tauren voulait qu'elle soit apportée au Cercle cénarien, aux Pitons du Tonnerre. Aller solliciter l'assistance de l'un des druides, là-bas.",
    [5021] = "Le discours incohérent de la femme fantomatique indique qu'elle a besoin qu'on livre un colis pour elle. Elle a dit que le colis se trouvait là où se trouve son cheval. Le fantôme est incompréhensible lorsqu'il s'agit de préciser où se trouve son cheval ou l'endroit où le paquet doit être livré.",
    [5022] = "Rendre visite aux Facteurs royaux de Hurlevent pour apprendre l'adresse d'une certaine Emma Gangrepierre. En général, il y a un officier du recensement à l'Hôtel de ville.",
    [5023] = "Se renseigner auprès des Surveillants royaux de Fossoyeuse pour apprendre l'adresse d'un certain Jeremiah Gangrepierre. En général, il y a un officier du recensement près du bureau d'inscription des guildes et tabards.",
    [5048] = "Trouver la vieille Emma à Hurlevent, et découvrir si elle est en fait Emma Gangrepierre. Si c'est le cas, elle appréciera peut-être le colis que Janice Gangrepierre lui a préparé.",
    [5049] = "Trouver Jeremiah Payson à Fossoyeuse et découvrir s'il est en fait Jeremiah Gangrepierre. Si c'est le cas, il appréciera peut-être le colis que Janice Gangrepierre lui a préparé.",
    [5050] = "Rapporter son Demi-charme porte-bonheur à Janice Gangrepierre dans les Maleterres de l'ouest.",
    [5051] = "Trouver l'autre moitié du Charme porte-bonheur quelque part dans le périmètre de la Ferme Gangrepierre dans les Maleterres de l'ouest et le recoller.\n\nDonner le Charme porte-bonheur à Janice Gangrepierre à la Ferme Gangrepierre, dans les Maleterres de l'ouest.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Charme porte-bonheur x 1",
    [5054] = "Storm Sabot-d'ombre, à Long-guet au Berceau-de-l'Hiver, veut que vous tuiez Ursius.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Ursius x 1",
    [5055] = "Storm Sabot-d'ombre, à Long-guet au Berceau-de-l'Hiver, veut que vous tuiez Brumeran.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Brumeran x 1",
    [5060] = "|Cffffd100Conditions :|r\n - Clé du cabinet de Dalson x 1",
    [5066] = "Trouvez le commandant Ashlam Vaillepoing. Il s'est installé au Camp du Noroît, au nord des montagnes d'Alterac.",
    [5082] = "Donova Antreneige du Berceau-de-l'Hiver veut que vous tuiez 8 Guides, 8 Protecteurs et 8 Totémiques de la tribu Tombe-hiver.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Guide tombe-hiver x 8\n - Protecteur tombe-hiver x 8\n - Totémique tombe-hiver x 8",
    [5083] = "Apportez le Flacon d'eau-de-feu vide à Donova Antreneige du Berceau-de-l'Hiver.",
    [5084] = "Chercher un indice dans le camp des furbolgs Mort-bois, dans le nord de Gangrebois.",
    [5085] = "Aller faire un rapport de vos trouvailles à Donova Antreneige.",
    [5086] = "Récolter 3 Gouttelettes d'Horreur toxique pour Donova Antreneige au Berceau-de-l'Hiver.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Gouttelette d'horreur toxique x 3",
    [5087] = "Trouver les Messagers Tombe-hiver et les empêcher de livrer leur cargaison au village Tombe-hiver.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Caisse des Tombe-hiver x 1",
    [5090] = "Trouvez le commandant Ashlam Vaillepoing. Il s'est installé au Camp du Noroît, au nord des montagnes d'Alterac.",
    [5091] = "Trouvez le commandant Ashlam Vaillepoing. Il s'est installé au Camp du Noroît, au nord des montagnes d'Alterac.",
    [5092] = "Tuer 10 Ecorcheurs Squelettes et 10 Goules écumantes sur la Colline des chagrins.\n\nRetourner auprès du commandant Ashlam Vaillepoing au camp du Noroît, dans les Maleterres de l'ouest.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Ecorcheur Squelette x 10\n - Goule écumante x 10",
    [5093] = "Partir à la recherche du Grand exécuteur Derrington. Son camp de base se trouve à la Barricade, à l'est des Clairières de Tirisfal et de Fossoyeuse.",
    [5094] = "Partir à la recherche du Grand exécuteur Derrington. Son camp de base se trouve à la Barricade, à l'est des Clairières de Tirisfal et de Fossoyeuse.",
    [5095] = "Partir à la recherche du Grand exécuteur Derrington. Son camp de base se trouve à la Barricade, à l'est des Clairières de Tirisfal et de Fossoyeuse.",
    [5096] = "Se rendre au camp de base de la Croisade écarlate situé entre le Champ de Gangrepierre et les Larmes de Dalson et détruire leur tente d'état-major.\n\nPlacer la bannière du Fléau sur le camp, puis revenir auprès du Grand exécuteur Derrington à la Barricade, dans les Maleterres de l'ouest.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Détruire la tente de commandement et planter l'étendard du Fléau dans le camp",
    [5097] = "À l'aide de la Torche-balise, marquer chaque tour à Andorhal ; il faut se tenir dans l'embrasure de la porte de la tour pour pouvoir la marquer.\n\nQuand les quatre tours seront marquées, rapporter la Torche-balise au commandant Ashlam Vaillepoing au camp du Noroît, dans les Maleterres de l'ouest.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Tour une marquée x 1\n - Tour deux marquée x 1\n - Tour trois marquée x 1\n - Tour quatre marquée x 1",
    [5098] = "À l'aide de la Torche-balise, marquer chaque tour à Andorhal ; il faut se tenir dans l'embrasure de la porte de la tour pour pouvoir la marquer.\n\nQuand les quatre tours seront marquées, rapporter la Torche-balise au Grand exécuteur Derrington à la Barricade, dans les Maleterres de l'ouest.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Tour une marquée x 1\n - Tour deux marquée x 1\n - Tour trois marquée x 1\n - Tour quatre marquée x 1",
    [5121] = "Donova Antreneige du Berceau-de-l'Hiver veut que vous triomphiez du Grand chef Tombe-hiver.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Grand chef Tombe-hiver x 1",
    [5149] = "Trouver la poupée de Pamela.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Poupée de Pamela x 1",
    [5152] = "Parler à Marlene, au sud des ruines d'Andorhal.",
    [5153] = "Apporter l'Alliance de Joseph à Chromie.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Alliance de Joseph x 1",
    [5154] = "Apportez les Annales de Comté-de-Darrow à Chromie, à Andorhal.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Annales de Comté-de-Darrow x 1",
    [5155] = "Entrer dans Jaedenar et tuer 4 Molosses de Jaedenar, 4 Gardiens de Jaedenar, 6 Adeptes de Jaedenar, et 6 Sectateurs de Jaedenar avant de retourner auprès de Greta Sabot-de-mousse dans Gangrebois.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Molosse de Jaedenar x 4\n - Gardien de Jaedenar x 4\n - Adepte de Jaedenar x 6\n - Sectateur de Jaedenar x 6",
    [5156] = "Trouver et détruire 2 Bêtes d'entropie et 2 Horreurs d'entropie tout en explorant le Val Grêlé. Après avoir vérifié que les cratères à cet endroit sont toujours remplis d'eau corrompue, retourner auprès de Taronn Plume-rouge dans Gangrebois.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Bête d'entropie x 2\n - Horreur d'entropie x 2\n - Explorer les cratères du Val Grêlé",
    [5157] = "Amener la Gourde vide au Puits de lune au centre de Jaedenar et la remplir avant de retourner voir Greta Sabot-de-mousse dans Gangrebois.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Eau de Puits de lune corrompue x 1",
    [5158] = "Apporter l'Eau de Puits de lune corrompue à Islen Oeil-des-rivières dans les Tarides.",
    [5159] = "Apporter l'Eau de Puits de lune purifiée à Greta Sabot-de-mousse dans Gangrebois.",
    [5163] = "Prendre le yéti mécanique d'Umi et effrayer ses amis avec :\n\nLegacki, à Long-guet (Berceau-de-l'Hiver)\nSaupoudr, à Gadgetzan (Tanaris)\nQuixxil, au refuge des Marshal (Cratère d'Un'Goro)\n\nQuand vous aurez terminé, rapporter le yéti mécanique à Umi.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Effrayer Legacki x 1\n - Effrayer Saupoudr x 1\n - Effrayer Quixxil x 1",
    [5165] = "À l'aide de l'Eau de Puits de lune purifiée, éteignez les flammes des quatre braseros de protection à l'intérieur du Fort des ombres dans Gangrebois. Retournez ensuite auprès de Greta Sabot-de-mousse au Sanctuaire d'émeraude.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Eteindre le Brasero de la Douleur x 1\n - Eteindre le Brasero de la Malice x 1\n - Eteindre le Brasero de la Souffrance x 1\n - Eteindre le Brasero de la Haine x 1",
    [5181] = "Rapporter le Crâne d'Horgus et l'Epée brisée de Marduk à Carlin Senterouge à la chapelle de l'Espoir de Lumière.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Crâne d'Horgus x 1\n - Epée brisée de Marduk x 1",
    [5210] = "Apporter les Annales complètes de Comté-de-Darrow à Carlin Senterouge dans les Maleterres de l'est.",
    [5211] = "Libérer 15 Esprits de Comté-de-Darrow et retourner auprès de Carlin Senterouge.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Esprits de Comté-de-Darrow libérés x 15",
    [5215] = "Parler à la grande prêtresse MacDonnell à la pointe du Noroît, dans les Maleterres de l'ouest.",
    [5216] = "Se rendre au Champ de Gangrepierre dans les Maleterres de l'ouest pour trouver et vaincre le Seigneur du Chaudron qui s'y trouve. Il aura peut-être une clé qui permet d'accéder au chaudron. Il faut se munir de la Bouteille du Champ de Gangrepierre vide pour pouvoir prélever un échantillon des poisons qui sont dans le chaudron.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Clé du chaudron du Champ de Gangrepierre x 1",
    [5217] = "Apporter une Bouteille du Champ de Gangrepierre remplie à la grande prêtresse MacDonnell au camp du Noroît, dans les Maleterres de l'ouest.",
    [5219] = "Se rendre aux Larmes de Dalson dans les Maleterres de l'ouest pour trouver et vaincre le Seigneur du Chaudron qui s'y trouve, puis utiliser sa clé pour accéder au chaudron. Il faut se munir de la Bouteille des Larmes de Dalson vide pour pouvoir prendre un échantillon des poisons qui sont dans le chaudron.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Clé du chaudron des Larmes de Dalson x 1",
    [5220] = "Apporter la Bouteille des Larmes de Dalson remplie à la grande prêtresse MacDonnell au camp du Noroît, dans les Maleterres de l'ouest.",
    [5222] = "Se rendre au Repaire putride dans les Maleterres de l'ouest pour trouver et vaincre le Seigneur du Chaudron qui s'y trouve, et utiliser sa clé pour accéder au chaudron. Il faut se munir de la Bouteille du Repaire putride vide pour pouvoir récupérer un échantillon des poisons à l'intérieur du chaudron.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Clé du chaudron du Repaire putride x 1",
    [5223] = "Apporter la Bouteille du Repaire putride remplie à la grande prêtresse MacDonnell au camp du Noroît, dans les Maleterres de l'ouest.",
    [5225] = "Se rendre à la Flétrissure de Gahrron dans les Maleterres de l'ouest pour trouver et vaincre le Seigneur du Chaudron qui s'y trouve, et utiliser sa clé pour accéder au chaudron. Il faut se munir de la Bouteille de la Flétrissure de Gahrron vide pour pouvoir récupérer un échantillon des poisons à l'intérieur du chaudron.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Clé du chaudron de la Flétrissure de Gahrron x 1",
    [5226] = "Apporter la Bouteille de la Flétrissure de Gahrron remplie à la grande prêtresse MacDonnell au camp du Noroît, dans les Maleterres de l'ouest.",
    [5228] = "Parler à la prêtresse des ombres Vandis à la Barricade, dans les Maleterres de l'ouest.",
    [5229] = "Se rendre au Champ de Gangrepierre, dans les Maleterres de l'ouest, pour trouver et vaincre le Seigneur du Chaudron qui s'y trouve. Il aura peut-être une clé qui permet d'accéder au chaudron. Il faut se munir de la Bouteille du Champ de Gangrepierre vide pour pouvoir prélever un échantillon des poisons qui sont dans le chaudron.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Clé du chaudron du Champ de Gangrepierre x 1",
    [5230] = "Apporter la Bouteille du Champ de Gangrepierre remplie à la prêtresse des ombres Vandis à la Barricade, dans les Maleterres de l'ouest.",
    [5231] = "Se rendre aux Larmes de Dalson dans les Maleterres de l'ouest pour trouver et vaincre le Seigneur du Chaudron qui s'y trouve, puis utiliser sa clé pour accéder au chaudron. Il faut se munir de la Bouteille des Larmes de Dalson vide pour pouvoir prendre un échantillon des poisons qui sont dans le chaudron.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Clé du chaudron des Larmes de Dalson x 1",
    [5232] = "Apporter la Bouteille des Larmes de Dalson remplie à la prêtresse des ombres Vandis à la Barricade, dans les Maleterres de l'ouest.",
    [5233] = "Se rendre au Repaire putride dans les Maleterres de l'ouest pour trouver et vaincre le Seigneur du Chaudron qui s'y trouve, et utiliser sa clé pour accéder au chaudron. Il faut se munir de la Bouteille du Repaire putride vide pour pouvoir récupérer un échantillon des poisons à l'intérieur du chaudron.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Clé du chaudron du Repaire putride x 1",
    [5234] = "Ramener la Bouteille du Repaire putride remplie à la prêtresse des ombres Vandis à la Barricade, dans les Maleterres de l'ouest.",
    [5235] = "Se rendre à la Flétrissure de Gahrron dans les Maleterres de l'ouest pour trouver et vaincre le Seigneur du Chaudron qui s'y trouve, et utiliser sa clé pour accéder au chaudron. Il faut se munir de la Bouteille de la Flétrissure de Gahrron vide pour pouvoir récupérer un échantillon des poisons à l'intérieur du chaudron.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Clé du chaudron de la Flétrissure de Gahrron x 1",
    [5236] = "Apporter la Bouteille de la Flétrissure de Gahrron remplie à la prêtresse des ombres Vandis à la Barricade, dans les Maleterres de l'ouest.",
    [5241] = "Trouver Carlin Senterouge.",
    [5242] = "Tuer Moora et Salia, et rapporter la Tête du Seigneur des ténèbres Gangr'dan à Greta Sabot-de-mousse dans Gangrebois.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Moora x 1\n - Salia x 1\n - Tête du seigneur des ténèbres Gangr'dan x 1",
    [5244] = "Parler à Jaron Sculptepierre au village de Pluie-d'Étoiles.",
    [5245] = "Utiliser la Pique de Jaron pour trouver les quatre Fragments de relique des Bien-nés. Les apporter à Aurora Mandeciel dans les Maleterres de l'est.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Fragment de la première relique x 1\n - Fragment de la deuxième relique x 1\n - Fragment de la troisième relique x 1\n - Fragment de la quatrième relique x 1",
    [5246] = "Trouver les Écrits sacrés des Bien-nés dans le temple de Zin-Malor en Azshara et les apporter à Aurora Mandeciel à la tour du Col du nord, dans les Maleterres de l'est.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Textes sacrés des Bien-nés x 1",
    [5249] = "Trouver le village de Pluie-d'Étoiles au Berceau-de-l'Hiver, et parler à Wynd Pistenuit.",
    [5250] = "Trouver le village de Pluie-d'Étoiles au Berceau-de-l'Hiver et parler à Wynd Pistenuit.",
    [5281] = "Trouver Egan. Vous savez seulement qu'on l'a vu pour la dernière fois aux alentours de Stratholme.",
    [5361] = "Apporter le Rapport du garde-falaise Longue-corne à Nataka Longue-corne au poste de Rôdeur-fantôme en Désolace.",
    [5381] = "Récupérer la Boîte démoniaque dans la forteresse de Hache-Tonnerre, puis la rapporter à Taiga Crin-sage, à Proie-de-l'Ombre.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Boîte démoniaque x 1",
    [5386] = "Parler à Jinar'Zillen à Proie-de-l'Ombre, puis apporter 2 poissons Ventre-rouge à Nataka Longue-corne.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Ventre-rouge x 2",
    [5421] = "|Cffffd100Conditions :|r\n - Crustacé x 5",
    [5501] = "Apporter à Bibbly F'ibulleur à la Hutte de Kormek 10 Os de kodo provenant du Cimetière des kodos.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Os de kodo x 10",
    [5510] = "|Cffffd100Conditions :|r\n - Pierre du Fléau des serviteurs x 20",
    [5527] = "Rendez-vous en Silithus et cherchez un Reliquaire de pureté dans les ruines du village de Sudevent. Si vous parvenez à le trouver, rapportez-le à Rabine Saturna à Havrenuit, Reflet-de-Lune.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Reliquaire de pureté x 1",
    [5533] = "Parler à l'alchimiste Arbington à la pointe du Noroît, dans les Maleterres de l'ouest.",
    [5534] = "Trouver \"une rune\" détenue par les nagas dans les ruines d'Eldarath et la rapporter à Kim'jael, en Azshara.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Une rune x 1",
    [5535] = "Détruire 6 Apparitions de Bien-nés et 6 Jeunes liches bien-nées avant de retourner auprès de Loh'atu en Azshara.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Apparition bien-née x 6\n - Jeune liche bien-née x 6",
    [5536] = "Tuer 6 Satyres Haldarr, 2 Entourloupeurs Haldarr, et 2 Gangreliges Haldarr puis revenir voir Loh'atu à la frontière d'Orneval.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Satyre Haldarr x 6\n - Entourloupeur Haldarr x 2\n - Gangrelige Haldarr x 2",
    [5537] = "Apporter 15 Fragments de squelette à l'alchimiste Arbington à la pointe du Noroît, dans les Maleterres de l'ouest.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Fragments de squelette x 15",
    [5542] = "Tuer 20 Chiens pestiférés chétifs, 5 Chiens pestiférés et 5 Chiens pestiférés frénétiques. Retourner voir Tirion Fordring quand la mission sera terminée.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Chien pestiféré chétif x 20\n - Chien pestiféré x 5\n - Chien pestiféré frénétique x 5",
    [5543] = "Tuer 30 Chauves-souris pestiférées, puis retourner voir Tirion Fordring.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Chauve-souris pestiférée x 30",
    [5544] = "Chercher les Larves putrides et les Dévoreurs de la région. Les tuer et récupérer leur viande. Retourner voir Tirion Fordring quand 15 pièces de Chair de Ver putride auront été récupérées.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Chair de ver putride x 15",
    [5581] = "Bannir 6 Portails, dans le convent de Mannoroc, puis retourner voir Taiga Crin-sage, à Proie-de-l'Ombre.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Portails bannis x 6",
    [5601] = "Trouver Pamela Senterouge à Comté-de-Darrow.",
    [5655] = "Parlez à Ur'kyo à Orgrimmar.",
    [5742] = "Écouter ce que Tirion Fordring a à dire.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Le conte de Tirion",
    [5781] = "Aller au caveau de Zaeldarr - situé dans la section la plus au sud des Maleterres de l'est - et récupérer le Marteau de Taelan. Retourner auprès de Tirion Fordring après avoir accompli la mission.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Marteau de Taelan x 1",
    [5845] = "Se rendre à Valnord, dans la région nord-est des Maleterres de l'est, et récupérer le Symbole de l'honneur perdu. Revenir auprès de Tirion Fordring quand la tâche est accomplie.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Symbole de l'honneur perdu x 1",
    [5846] = "Voyager jusqu'à l'île de Caer Darrow, dans la région centre-sud des Maleterres, et chercher un indice de l'emplacement du tableau.",
    [5863] = "Andi Lynn de Gadgetzan veut que vous détruisiez la Base des Cognedunes en tuant 10 Brutes Cognedunes, 10 Massacreurs Cognedunes, et Gor'marok le Ravageur.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Brute Cognedune x 10\n - Massacreur Cognedune x 10\n - Gor'marok le Ravageur x 1",
    [5882] = "|Cffffd100Conditions :|r\n - Fragment d'âme corrompue x 6",
    [5901] = "Utiliser le Pot de Termites vide sur la Termitière, dans les Maleterres de l'est. Après avoir récupéré 100 Termites des Maleterres, retourner voir Mickey Levine, à la Barricade, dans les Clairières de Tirisfal.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Termites des Maleterres x 100",
    [5902] = "Trouver un endroit judicieux au centre de la scierie de la Crête du nord, dans les Maleterres de l'ouest, pour placer le Tonneau de Termites des Maleterres.",
    [5903] = "Utiliser le Pot de Termites vide sur la Termitière, dans les Maleterres de l'est. Après avoir récupéré 100 Termites des Maleterres, retourner voir Nathaniel Dumah, au camp du Noroît, dans les Maleterres de l'ouest.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Termites des Maleterres x 100",
    [5904] = "Trouver un endroit propice, au centre de la scierie de la Crête du nord, dans les Maleterres de l'ouest, pour placer le Tonneau de Termites des Maleterres.",
    [5943] = "Escorter la caravane de Gizelton à travers le convent de Mannoroc. Parler à Smeed, au camp de Fouillevis, pour toucher la récompense.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Escorter la caravane de Gizelton au-delà du convent de Mannoroc",
    [6004] = "Tuer deux Médecins écarlates, deux Chasseurs écarlates, deux Magi écarlates et deux Chevaliers écarlates avant de revenir vers Kirsta Fondombre dans les Maleterres de l'ouest.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Médecin écarlate x 2\n - Chasseur écarlate x 2\n - Mage écarlate x 2\n - Chevalier écarlate x 2",
    [6021] = "Apportez la Tête de Zaeldarr au Conservateur Alen, à la chapelle de l'Espoir de Lumière.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Tête de Zaeldarr x 1",
    [6022] = "Allez à l'est à la Croisée de Corin et détruisez les membres du Fléau qui habitent la ville. Rassemblez 7 morceaux de Pourriture vivante en moins de 10 minutes. Utilisez le Mortier et le Pilon pour réduire la Pourriture vivante en Pourriture coagulée.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Pourriture coagulée x 1",
    [6023] = "Tuer le veneur Radley et le cavalier Durgen, avant de retourner voir Kirsta Fondombre, dans les Maleterres de l'ouest.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Veneur Radley x 1\n - Cavalier Durgen x 1",
    [6024] = "Tuer l'Infiltrateur Hameya. Utiliser sa clé sur le Monticule de poussière, derrière le caveau de Zaeldarr.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Clé d'Hameya x 1",
    [6025] = "Aller à Âtreval, et trouver un poste d'observation élevé d'où voir la ville et jauger les forces de la Croisade écarlate, puis retourner voir Kirsta Fondombre, dans les Maleterres de l'ouest.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Surveiller Âtreval d'un poste d'observation élevé",
    [6026] = "Smokey LaRue veut que vous lui apportiez 2 Barres de thorium, 1 Bâtonnet doré, 8 Bombes explosives et 8 Déclencheurs instables.\n\nVous devrez trouver un forgeron et un ingénieur pour obtenir ces objets.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Barre de thorium x 2\n - Bâtonnet doré x 1\n - Bombe explosive x 8\n - Déclencheur instable x 8",
    [6027] = "Obtenir le Livre des Anciens, puis le rapporter à Azore Aldamort, dans la tour d'Ethel Rethor.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Livre des Anciens x 1",
    [6028] = "Apportez le Rapport de Long-guet à l'officier de l'Aube d'argent Purecarde au Camp du Noroît, dans les Maleterres de l'ouest.",
    [6029] = "Livrer le Rapport de Long-guet à l'officier Garush de l'Aube d'argent, à la Barricade, dans les Maleterres de l'ouest.",
    [6030] = "Apportez le livre « Études sur la langue des Esprits » au duc Nicholas Zverenhoff, à la chapelle de l'Espoir de Lumière, dans les Maleterres de l'est.",
    [6041] = "Aller à Pestebois, au nord-ouest de la chapelle de l'Espoir de Lumière. Détruire 8 Bâtiments du Fléau en utilisant le Mélange spécial de Smokey sur les Marques de détonation que portent les bâtiments destinés à être détruits. Smokey a fait marquer les ziggourats et les abattoirs.\n\nUne fois la bombe placée, quitter le bâtiment et contempler le résultat de votre opération.\n\nGarder en tête que le Mélange spécial de Smokey est très... instable.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Constructions détruites du Fléau x 8",
    [6042] = "Tuer 20 Chauves-souris pestiférées nuisibles et 10 Chauves-souris pestiférées monstrueuses. Retourner voir Nathanos le Flétrisseur quand ce sera fait.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Chauve-souris pestiférée nocive x 20\n - Chauve-souris pestiférée monstrueuse x 10",
    [6133] = "Aller vers la frontière nord des Maleterres de l'est et récupérer le Registre de Quel'Thalas. L'objet se trouve quelque part dans le gîte de Quel'Lithien.\n\nTuer également 8 Forestiers, 8 Eclaireurs et 8 Bûcherons.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Eclaireur x 8\n - Forestier x 8\n - Bûcheron x 8\n - Registre de Quel'Thalas x 1",
    [6134] = "Apporter 8 Fantôm-o-plasmes et la Caisse d'aimants fantômes à Hornizz Razzkankan, en Désolace.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Fantôm-o-plasme x 8",
    [6135] = "Trouver Crépuscaile et la tuer. Récupérer une Touffe de fourrure de Crépuscaile sur son cadavre, puis l'apporter à Nathanos le Flétrisseur.\n\nIl paraît que Crépuscaile hante les terres qui se trouvent au nord de la ferme des Marris.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Touffe de fourrure de Crépuscaile x 1",
    [6136] = "Trouver Creusechair, dans les terres au nord des Maleterres de l'est, et le tuer. Retourner voir Nathanos le Flétrisseur quand la mission sera terminée.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Creusechair x 1",
    [6141] = "Adressez-vous au frère Anton à la Combe de Nijel, en Désolace.",
    [6142] = "Trouver 10 morceaux de Chair de praire et les apporter à Mai'Lahii, à Proie-de-l'Ombre.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Chair de praire x 10",
    [6143] = "Tuer 7 Myrmidons Ondulame, 7 Nagas Ondulame et 5 Sorcières Ondulame, puis parler à Drulzegar, à Proie-de-l'Ombre, en Désolace.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Myrmidon Ondulame x 7\n - Naga Ondulame x 7\n - Sorcière Ondulame x 5",
    [6162] = "Tuer le Seigneur Ror et apporter sa Griffe à Dreka'Sur, au Poste de la Vénéneuse, en Gangrebois.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Griffe du Seigneur Ror x 1",
    [6164] = "Trouver le Livre de compte d'Augustus à l'Auberge, au Val-terreur.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Livre de compte d'Augustus x 1",
    [6182] = "Parler à Mathias Shaw, dans la vieille ville de Hurlevent. Il se trouve dans la caserne.",
    [6183] = "Reparlez à Mathias Shaw, quand vous aurez décidé d'accepter son travail.",
    [6184] = "Allez au camp du Noroît, dans les Maleterres de l'ouest, et rencontrez votre contact, Flint Ombrelongue.",
    [6185] = "Fouillez les Maleterres de l'est pour trouver des indices relatifs au \"Flétrisseur\" et aux agents du SI:7 portés disparus. Si vous trouvez un quelconque Insigne du SI:7, rapportez-le à Flint Ombrelongue, au camp du Noroît.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Insigne du SI:7 (Rutger) x 1\n - Insigne du SI:7 (Fredo) x 1\n - Insigne du SI:7 (Turyen) x 1\n - Le Flétrisseur découvert",
    [6186] = "Retourner à Hurlevent et informer le roi Varian Wrynn du destin de Nathanos Marris.",
    [6187] = "Montez une armée et portez-vous vers les Maleterres de l'est. Lancez un assaut total sur Nathanos le Flétrisseur et détruisez tout misérable élément de la Horde qui tenterait de le défendre.\n\nGardez votre présence d'esprit, Tomcatdk : la Horde le défendra jusqu'à la mort.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Nathanos le Flétrisseur x 1",
    [6389] = "Libérez les Termites des Maleterres dans la scierie de la Crête du nord, puis retournez voir Nathaniel Dumah, au camp du Noroît, dans les Maleterres de l'ouest, avec le Tonneau de Termites des Maleterres.",
    [6390] = "Libérez les Termites des Maleterres dans la scierie de la Crête du nord, puis retournez voir Mickey Levine, à la Barricade, dans les Clairières de Tirisfal, avec le Tonneau de Termites des Maleterres.",
    [6622] = "Sauver des patients en utilisant des Bandages de Triage, afin de panser leurs blessures. Vous devez sauver 15 patients avant que 6 d'entre eux ne meurent de leurs blessures.\n\nRappelez-vous l'ordre du triage ! Les Soldats agonisants meurent les premiers, suivis des Soldats grièvement blessés. Les Soldats blessés sont ceux qui meurent les derniers, et ils doivent être soignés APRÈS ceux qui agonisent et ceux qui sont grièvement blessés ; selon le tri que vous avez fait.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - 15 patients sauvés !",
    [6624] = "Sauver des patients en utilisant des Bandages de Triage, afin de panser leurs blessures. Vous devez sauver 15 patients avant que 6 d'entre eux ne meurent de leurs blessures.\n\nRappelez-vous l'ordre du triage ! Les Soldats agonisants meurent les premiers, suivis des Soldats grièvement blessés. Les Soldats blessés sont ceux qui meurent les derniers, et ils doivent être soignés APRES ceux qui agonisent et ceux qui sont grièvement blessés ; selon le tri que vous avez fait.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - 15 patients sauvés !",
    [6761] = "Parler à Mathrengyl Pasdelours, dans l'Enclave cénarienne de Darnassus.",
    [6762] = "Parler à Rabine Saturna, au village de Havrenuit, à Reflet-de-Lune. Reflet-de-Lune se trouve entre Gangrebois et le Berceau-de-l'Hiver. Le chemin qui y mène traverse le Repaire des Grumegueules.",
    [6844] = "Apporter l'Objet silithide incrusté à Umber, l'archiviste du Cercle cénarien, au village de Havrenuit, à Reflet-de-Lune.",
    [6845] = "Parler à Rabine Saturna, au village de Havrenuit, à Reflet-de-Lune.",
    [7003] = "Utilisez l'Ultra-réducteur de Zorbin sur des géants de n'importe quel type en Féralas afin les rendre plus faciles à gérer. Apportez 15 Résidus de miniaturisation que vous trouverez sur les versions rétrécies des géants à Zorbin Poudrozieu aux docks de la Côte oubliée en Féralas.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Résidus de miniaturisation x 15",
    [7492] = "Parlez à Talo Sabot-de-ronce au Camp Mojache, en Féralas.",
    [7601] = "Parlez à Démonet à Gangrebois.",
    [7701] = "Loin dans les profondeurs de la Fosse aux scories dans le Chaudron, dans la Gorge des Vents brûlants, vous trouverez le nain traître du clan Sombrefer répondant au nom de surveillant Maltorius.\n\nTuez le surveillant Maltorius et prenez sa tête. Apportez-la au capitaine de guet Lolo Heurteloin dans la Gorge des Vents brûlants pour la récompense.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Tête du surveillant Maltorius x 1",
    [7702] = "Loin dans les profondeurs de la Fosse aux scories, dans le Chaudron, vous trouverez le dortoir des nains Sombrefer.\n\nAllez dans leur dortoir et volez 20 Oreillers Sombrefer. Revenez voir Evonice Fumesuie dans la gorge des Vents brûlants quand vous aurez accompli cette tâche.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Oreiller sombrefer x 20",
    [7721] = "Récupérez 10 Noyaux élémentaires d'eau sur les Elémentaires marins et les Embruns le long de la Côte oubliée de Féralas, et rapportez-les à Zorbin Poudrozieu, près du quai là-bas.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Noyau d'élémentaire d'eau x 10",
    [7722] = "Quelque part dans la Fosse aux scories du Chaudron, vous trouverez les Plans secrets : Flux embrasé dérobés par le surveillant Maltorius. Trouvez ces plans et rapportez-les au Maître forgeron Brûlotin, dans la Gorge des Vents brûlants.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Plans secrets : Flux embrasé x 1",
    [7723] = "Hansel Lourdemains veut que vous tuiez 20 Golems de guerre lourds.\n\nLes golems de guerre lourds errent dans les régions qui entourent le Chaudron dans la Gorge des Vents brûlants. Quand vous en aurez tué assez, revenez voir Hansel dans la Gorge des Vents brûlants.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Golem de guerre lourd x 20",
    [7724] = "Hansel Lourdemains veut que vous tuiez 20 Grandes araignées de lave dans la gorge des Vents brûlants. Vous pouvez trouver les Grandes araignées de lave dans les plateaux et les mares de lave de la gorge des Vents brûlants.\n\nRevenez voir Hansel dans la gorge des Vents brûlants quand vous aurez tué assez de Grandes araignées de lave.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Grande araignée de lave x 20",
    [7727] = "Hansel Lourdemains veut que vous tuiez 20 Incendosaures.\n\nOn peut les trouver dans les bassins de lave de la Fosse aux scories, qui occupe une partie du Chaudron de la gorge des Vents brûlants.\n\nRetournez voir Hansel Lourdemains dans la gorge des Vents brûlants lorsque vous aurez tué suffisamment d'Incendosaures.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Incendosaure x 20",
    [7728] = "Trouvez la Tuyère de forge et la Longue-vue du guetteur et rapportez-les au sous-chef Scrange dans la gorge des Vents brûlants.\n\nLes seules informations dont vous disposez sur ces objets sont les suivantes : ils ont été dérobés par les nains Sombrefer, la Tuyère de forge est un outil utilisé par les forgerons et la Longue-vue du guetteur est un outil de surveillance inestimable pour les guetteurs.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Tuyère de forge x 1\n - Longue-vue de guetteur x 1",
    [7729] = "Tuez 15 Sous-chefs Sombrefer et 15 Esclavagistes Sombrefer. Revenez voir le sous-chef Scrange dans la gorge des Vents brûlants quand vous aurez accompli cette tâche.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Sous-chef Sombrefer x 15\n - Esclavagiste Sombrefer x 15",
    [7730] = "Rapportez 20 Carapaces de Zukk'ash à Hadoken Rôdeur-agile au Camp Mojache en Féralas.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Carapace de Zukk'ash x 20",
    [7731] = "Battez Fouet-cuisant dans le Gouffre grouillant et rapportez ses glandes à Hadoken Rôdeur-agile au Camp Mojache en Féralas.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Glandes de Fouet-cuisant x 1",
    [7732] = "Apportez le rapport sur les Zukk'ash du Camp Mojache à Zilzibin Kongadok. Il réside à la Herse d'Orgrimmar.",
    [7733] = "Fournir 10 Peaux de yéti Griffe Féroce à Pratt McGrubben au bastion de Pennelune, en Féralas.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Peau de yéti griffe féroce x 10",
    [7734] = "Fournir 10 Peaux de yéti Griffe Féroce à Jangdor Rôdeur-agile au Camp Mojache, en Féralas.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Peau de yéti griffe féroce x 10",
    [7735] = "Apportez la Peau de yéti en parfait état à Pratt McGrubben au bastion de Pennelune, en Féralas.",
    [7738] = "Apportez la Peau de yéti parfaite à Jangdor Rôdeur-agile au Camp Mojache, en Féralas.",
    [7815] = "Katoom le pêcheur, du village des Vengebroches des Hinterlands, veut que vous tuiez 10 tortues Gueules d’acier des mers. Retournez le voir lorsque vous aurez terminé.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Gueule d'acier des mers x 10",
    [7816] = "Katoom le pêcheur, du village des Vengebroches dans les Hinterlands, veut que vous tuiez Gammerita et que vous récupériez le Meilleur appât de Katoom sur son cadavre.\n\nKatoom a mentionné que Gammerita rôdait sur la côte, avec d'autres tortues.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Le meilleur appât de Katoom x 1",
    [7817] = "|Cffffd100Conditions :|r\n - Etoffe de tisse-mage x 60",
    [7828] = "Le veneur Markhor du village des Vengebroches, dans les Hinterlands, veut que vous tuiez 5 Traqueurs Crins-d'argent et 5 Hurleurs Crins-d'argent. Retournez le voir lorsque vous aurez terminé.\n\nD'après Markhor, les loups se cachent dans les régions sauvages des Hinterlands.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Traqueur Crins-d'argent x 5\n - Hurleur Crins-d'argent x 5",
    [7829] = "Le veneur Markhor du village des Vengebroches des Hinterlands veut que vous tuiez 10 Chouettards sauvages. Retournez le voir lorsque vous aurez terminé.\n\nMarkhor a mentionné que les Chouettards sauvages vivaient dans les régions sauvages des Hinterlands.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Chouettard sauvage x 10",
    [7830] = "Le veneur Markhor, du village des Vengebroches des Hinterlands, veut que vous tuiez un Seigneur du ciel Bec-rasoir et que vous récupériez une Plume de Seigneur du ciel. Revenez le voir lorsque vous aurez terminé votre tâche.\n\nD'après Markhor, les Seigneurs du ciel Bec-rasoir sont des créatures rares qui vivent dans les régions sauvages des Hinterlands.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Plume de seigneur du ciel x 1",
    [7839] = "Le forgeron Boiscorie du village des Vengebroches, dans les Hinterlands, vous demande de retrouver les Outils perdus de Boiscorie. Retournez le voir lorsque vous les aurez.\n\nIl a laissé entendre qu'ils pourraient se trouver dans l'un des temples des Vilebranches, au nord-est des Hinterlands. Vous devriez aussi jeter un coup d'œil au rocher de l'Affût.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Outils perdus de Boiscorie x 1",
    [7840] = "Lard, du village des Vengebroches, dans les Hinterlands, veut que vous retrouviez le Repas de Lard. Retournez le voir lorsque ce sera fait.\n\nD'après Lard, il l'aurait laissé dans l'île, au nord. Prenez garde aux trolls Vilebranche !\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Repas de Lard x 1",
    [7841] = "Otho Moji'ko du village des Vengebroches dans les Hinterlands veut que vous massacriez 8 Eclaireurs du Haut-val, 5 Estafettes du Haut-val, 5 Forestiers du Haut-val et 2 Tireurs d'élite du Haut-val. Revenez le voir lorsque vous aurez terminé.\n\nVous pouvez trouver les elfes du Haut-val au Gîte de Quel’Danil, au nord-ouest des Hinterlands.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Eclaireur du Haut-val x 8\n - Estafette du Haut-val x 5\n - Forestier du Haut-val x 5\n - Tireur d'élite du Haut-val x 2",
    [7842] = "Otho Moji'ko, du village des Vengebroches dans les Hinterlands, vous demande de lui rapporter 1 Longue plume élégante prélevée sur l'un des griffons qui vivent dans les Hinterlands. Retournez le voir lorsque vous l'aurez.\n\nOn trouve des griffons dans tous les Hinterlands.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Longue plume élégante x 1",
    [7843] = "Otho Moji'ko, du village des Vengebroches dans les Hinterlands, vous demande de vous rendre au Nid-de-l'Aigle, à l'ouest des Hinterlands. Vous devez placer le Message final pour les Marteaux-hardis près du puits, au cœur de la ville. Retournez le voir lorsque vous aurez accompli votre tâche.\n\nFaites attention aux nains Marteaux-hardis, qui risquent de mal le prendre, et aux autres membres de l'Alliance.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Message pour les Marteaux-hardis transmis",
    [7844] = "Yayo’jin la Mystique, du village des Vengebroches des Hinterlands, veut que tuiez 6 Scalpeurs Vilebranche et 2 Devins Vilebranche. Retournez la voir lorsque vous aurez terminé.\n\nD’après Yayo’jin, ces trolls se trouvent près des temples de Shaol'watha et d’Agol'watha, au nord-nord-est des Hinterlands.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Scalpeur vilebranche x 6\n - Devin vilebranche x 2",
    [8151] = "Parlez à Ogtinc en Azshara.",
    [8153] = "Rapporter deux Ramures de coursier parfaites à Ogtinc en Azshara. Ogtinc vit au sommet des collines au nord-est des ruines d'Eldarath.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Ramure de coursier parfaite x 2",
    [8231] = "Apporter 6 Écailles de vandale des flots à Ogtinc en Azshara. Ogtinc demeure au sommet des falaises au nord-est des ruines d'Eldarath.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Ecailles de vandale des flots x 6",
    [8250] = "Trouver Sanath Lim-yo et obtenir d'être mené à l'archimage Xylem.",
    [8251] = "Procurer 10 doses de Poussière lumineuse à l'archimage Xylem.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Poussière lumineuse x 10",
    [8252] = "Récupérer 6 Coraux enchantés et les apporter à l'archimage Xylem.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Corail enchanté x 6",
    [8254] = "Chercher Ogtinc en Azshara.",
    [8255] = "Récupérer 4 Glandes de coursier en bonne santé et les apporter à Ogtinc en Azshara. Ogtinc réside au sommet des falaises, au nord-est des ruines d'Eldarath.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Glande de coursier en bonne santé x 4",
    [8256] = "Récupérer une dose d'Ichor de non-mort pour le compte d'Ogtinc en Azshara. Ogtinc réside au sommet des falaises, au nord-est des ruines d'Eldarath.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Ichor de non-mort x 1",
    [8275] = "Parler à l'imploratrice céleste Corne-fière au Fort cénarien, en Silithus.",
    [8276] = "Parler à l'imploratrice céleste Corne-fière au Fort cénarien, en Silithus.",
    [8277] = "Beetix Kalefragile au Fort cénarien en Silithus veut que vous ramassiez 8 Dards de scorpide Cinglepierre et 8 Crocs de Glisseuse du sable. Retournez la voir quand vous aurez terminé.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Dard de scorpide cinglepierre x 8\n - Croc de glisseuse du sable x 8",
    [8278] = "Beetix Kalefragile, du Fort cénarien en Silithus, veut que vous ramassiez 3 Dards de pinceur Cinglepierre, 3 Dards d'écorcheur Cinglepierre et 3 Crocs de traqueuse des rochers.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Dard d'écorcheur cinglepierre x 3\n - Dard de pinceur cinglepierre x 3\n - Croc de traqueuse des rochers x 3",
    [8279] = "Apporter les trois chapitres du Lexique du crépuscule à l'ermite Ortell, en Silithus.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Lexique du crépuscule - Chapitre 1 x 1\n - Lexique du crépuscule - Chapitre 2 x 1\n - Lexique du crépuscule - Chapitre 3 x 1",
    [8280] = "L'Imploratrice céleste Corne-fière au Fort cénarien en Silithus veut que vous tuiez 15 Frappeurs térébrants.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Frappeur térébrant x 15",
    [8281] = "L'Imploratrice céleste Corne-fière au Fort cénarien en Silithus veut que vous tuiez 20 Ecraseurs térébrants.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Ecraseur térébrant x 20",
    [8282] = "Récupérez la Sacoche de Noggle dans les montagnes au sud de Silithus et rapportez-la-lui au Fort cénarien.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Sacoche de Noggle x 1",
    [8283] = "Apporter la Pince de Poigne-de-mort à Vish Kozus, capitaine de la garde.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Pince de Poigne-de-mort x 1",
    [8284] = "La géologue Fablemort du Fort cénarien en Silithus veut que vous rassembliez 8 Fragments de Tablette du crépuscule.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Fragment de la tablette du Crépuscule x 8",
    [8285] = "Apporter la tablette réparée à l'ermite Ortell à Silithus.",
    [8287] = "Remettez la Transcription décodée de la tablette au commandant Mar'alith.",
    [8304] = "Le commandant Mar'alith, du Fort cénarien en Silithus, veut que vous interrogiez les habitants du Campement de Barbe-de-bronze, qui se trouve au sud du Fort cénarien.\n\nLorsque vous aurez réuni les informations demandées, retournez voir le commandant Mar'alith.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Frankal interrogé x 1\n - Rutgar interrogé x 1",
    [8308] = "Apporter la Lettre de Brann Barbe-de-bronze au Campement de Barbe-de-bronze dans le sud de Silithus.",
    [8318] = "Apporter 10 Textes du crépuscule cryptés à Bor Crin-sauvage au Fort cénarien.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Texte du crépuscule crypté x 10",
    [8320] = "Tuer 10 Géoseigneurs du crépuscule, puis retourner voir Huum Crin-sauvage au Fort cénarien.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Géoseigneur du crépuscule x 10",
    [8323] = "L'ermite Ortell veut que vous lui apportiez 10 Textes du crépuscule cryptés.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Texte du crépuscule crypté x 10",
    [8361] = "Bor Crin-sauvage, du Fort cénarien en Silithus, veut que vous détruisiez un Templier abyssal et que vous lui rameniez un Blason abyssal comme preuve de votre exploit.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Blason abyssal x 1",
    [8365] = "Hautaine Lamodiste veut que vous lui rapportiez 20 Chapeaux de pirate des mers du Sud à Port Gentepression, en Tanaris.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Chapeau de pirate des mers du Sud x 20",
    [8366] = "Le chef de la sécurité Vizisanie du port Gentepression, en Tanaris, veut que vous tuiez 10 Pirates des mers du Sud, 10 Flibustiers des mers du Sud, 10 Portefaix des mers du Sud et 10 Bretteurs des mers du Sud.\n|Cffffd100 |r\n|Cffffd100Conditions :|r\n - Pirate des mers du Sud x 10\n - Flibustier des mers du Sud x 10\n - Portefaix des mers du Sud x 10\n - Bretteur des mers du Sud x 10",
