    [5241] = "Encontre Carlin Trilharrubra.",
    [5242] = "Mate Moora e Salia, e leve a Cabeça do Lorde das Sombras Fel'dan para Greta Musgo no Casco em Selva Maleva.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Moora x 1\n - Salia x 1\n - Cabeça do Lorde das Sombras Fel'Dan x 1",
    [5244] = "Fale com Jaron Moldapedra na Aldeia Chuva Estelar.",
    [5245] = "Use a Picareta do Jaron para encontrar os quatro Fragmentos da Relíquia dos Altaneiros. Leve-os para Aurora Clamacéus nas Terras Pestilentas Orientais.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Primeiro Fragmento de Relíquia x 1\n - Segundo Fragmento de Relíquia x 1\n - Terceiro Fragmento de Relíquia x 1\n - Quarto Fragmento de Relíquia x 1",
    [5246] = "Encontre os Escritos Sagrados dos Altaneiros no Templo de Zin-Malor, em Azshara, e leve-os para Aurora Clamacéus, perto da Torre do Passo Norte nas Terras Pestilentas Orientais.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Escritos Sagrados dos Altaneiros x 1",
    [5249] = "Encontre a Aldeia Chuva Estelar, em Hibérnia, e fale com Eol Correnoite.",
    [5250] = "Encontre a Chuva Estelar, em Hibérnia, e fale com Eol Correnoite.",
    [5281] = "Encontre Egan. Ele foi visto pela última vez nos arredores de Stratholme.",
    [5361] = "Leve o Relatório de Vigia do Rochedo Chifre Longo para Nataoka Chifre Longo no Entreposto do Espírito que Anda, situado em Desolação.",
    [5381] = "Recolha a Caixa Demoníaca na Fortaleza Machado do Trovão e leve-a, junto com a Picareta do Diabo, para Taiga Crina Sábia na Aldeia Pescassombra.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Caixa Demoníaca x 1",
    [5386] = "Converse com Jinar'Zillen, na Aldeia Pescassombra, e depois leve 2 peixes pança-sangrenta para Nataoka Chifre Longo.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Peixe Pança-sangrenta x 2",
    [5421] = "|Cffffd100Requisitos:|r\n - Lagostim x 5",
    [5501] = "Entregue 10 Ossos de Kodo do Cemitério dos Kodos para Bibbly Ferrafivela no Barraco do Kormek.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Osso de Kodo x 10",
    [5510] = "|Cffffd100Requisitos:|r\n - Pedra do Flagelo do Lacaio x 20",
    [5527] = "Viaje até Silithus e procure um Relicário da Pureza dentro das ruínas da Vila do Vento Sul. Se conseguir encontrá-lo, regresse com ele até Ravine Saturna, no Refúgio Noturno, que fica na Clareira da Lua.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Relicário da Pureza x 1",
    [5533] = "Fale com o Alquimista Arvignon no Morro do Ventogelante, que fica nas Terras Pestilentas Ocidentais.",
    [5534] = "Encontre Essa Tal Runa que está com as nagas nas Ruínas de Eldarath e traga-a para Kam'arawn, em Azshara.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Essa Tal Runa x 1",
    [5535] = "Destrua 6 Aparições Altaneiras e 6 Subliches Altaneiros. Depois, fale com Loh'atu em Azshara.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Aparição Altaneira x 6\n - Sub-lich Altaneiro x 6",
    [5536] = "Mate 6 Sátiros Haldarr, 2 Trapaceiros Haldarr e 2 Devotos Vil Haldarr. Depois, fale novamente com Loh'atu na fronteira do Vale Gris.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Sátiro Haldarr x 6\n - Trapaceiro Haldarr x 2\n - Devoto Vil Haldarr x 2",
    [5537] = "Leve 15 Fragmentos de Ossos para o Alquimista Arvignon no Morro do Ventogelante, que fica nas Terras Pestilentas Ocidentais.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Fragmentos de Ossos x 15",
    [5542] = "Mate 20 Cães Pestilentos Miúdos, 5 Cães Pestilentos e 5 Cães Pestilentos Frenéticos. Fale novamente com Tirion Fordring quando a tarefa estiver concluída.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Cão Pestilento Miúdo x 20\n - Cão Pestilento x 5\n - Cão Pestilento Frenético x 5",
    [5543] = "Mate 30 Quirópragos e fale novamente com Tirion Fordring.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Quiróprago x 30",
    [5544] = "Procure os Vermes Carniceiros e os Devoradores de Carniça da região. Mate-os e tire a carne. Fale novamente com Tirion Fordring depois que tiver juntado 15 Nacos de Carne de Verme Carniceiro.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Naco de Carne de Verme Carniceiro x 15",
    [5581] = "Feche 6 portais nas Ruínas de Mannoroc e fale com Taiga Crina Sábia na Aldeia Pescassombra.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Portais banidos x 6",
    [5601] = "Encontre Pâmela Trilharrubra na Vila das Flechas.",
    [5655] = "Fale com Ur'kyo em Orgrimmar.",
    [5742] = "Escute o que Tirion Fordring tem a dizer.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - O conto de Tirion",
    [5781] = "Viaje para a Chácara Secreta, no extremo sul das Terras Pestilentas Orientais, e recupere o Martelo de Taelan. Fale novamente com Tirion Fordring ao completar seu objetivo.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Martelo de Taelan x 1",
    [5845] = "Viaje para o Vilarejo do Norte, na região nordeste das Terras Pestilentas Orientais, e recupere o Símbolo da Honra Perdida. Fale novamente com Tirion Fordring ao completar seu objetivo.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Símbolo da Honra Perdida x 1",
    [5846] = "Viaje para a ilha de Castro das Flechas, na região centro-sul das Terras Pestilentas, e procure pistas sobre o paradeiro da pintura.",
    [5863] = "Andi Lynn em Geringontzan quer que você destrua o Complexo Dunamalho. Para isso, deve matar 10 Brutamontes Dunamalho, 10 Impositores Dunamalho e Gor'marok, o Assolador.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Brutamontes Dunamalho x 10\n - Impositor Dunamalho x 10\n - Gor'marok, o Assolador x 1",
    [5882] = "|Cffffd100Requisitos:|r\n - Estilhaço de Alma Corrompida x 6",
    [5901] = "Use a Jarra de Cupins Vazia nos Cupinzeiros que há nas Terras Pestilentas Orientais. Após conseguir 100 Cupins das Terras Pestilentas, fale novamente com Guel Levine no Baluarte, nas Clareiras de Tirisfal.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Cupins das Terras Pestilentas x 100",
    [5902] = "Encontre um lugar apropriado para pôr o Barril de Cupins das Terras Pestilentas no centro da Serraria de Beiranorte, que fica nas Terras Pestilentas Ocidentais.",
    [5903] = "Use a Jarra de Cupins Vazia nos Cupinzeiros que há nas Terras Pestilentas Orientais. Após conseguir 100 Cupins das Terras Pestilentas, fale novamente com Nataniel Dumah no Acampamento Ventogelante, em Terras Pestilentas Ocidentais.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Cupins das Terras Pestilentas x 100",
    [5904] = "Encontre um lugar apropriado para pôr o Barril de Cupins das Terras Pestilentas no centro da Serraria de Beiranorte, que fica nas Terras Pestilentas Ocidentais.",
    [5943] = "Escolte a Caravana Gizelton pelas Ruínas de Mannoroc. Fale com o Smid no Acampamento do Rabiscafuso para receber a recompensa.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Escolte a Caravana Gizelton pelas Ruínas de Mannoroc.",
    [6004] = "Mate 2 Socorristas Escarlate, 2 Caçadores Escarlate, 2 Magos Escarlate e 2 Cavaleiros Escarlate antes de voltar a Kirsta Sombrafunda, nas Terras Pestilentas Ocidentais.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Socorrista Escarlate x 2\n - Caçador Escarlate x 2\n - Mago Escarlate x 2\n - Cavaleiro Escarlate x 2",
    [6021] = "Leve a Cabeça de Zaeldarr ao Zelador Alen na Capela Esperança da Luz.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Cabeça de Zaeldarr x 1",
    [6022] = "Vá ao Cruzamento de Corin, a leste, e mate membros do Flagelo que habitam a cidade. Colete 7 pedaços de Putrefação Viva em menos de 10 minutos. Use o Almofariz e Pilão para moer a Putrefação Viva, de maneira a transformá-la em Putrefação Coagulada.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Putrefação Coagulada x 1",
    [6023] = "Mate o Guarda-caça Eiras e o Fidalgo Durgen antes de voltar à Kirsta Sombrafunda, nas Terras Pestilentas Ocidentais.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Guarda-caça Eiras x 1\n - Fidalgo Durgen x 1",
    [6024] = "Mate o Infiltrador Hameya. Use a chave dele no Monturo de Terra atrás da Chácara Secreta.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Chave de Hameya x 1",
    [6025] = "Entre em Amparo e encontre um lugar alto de onde possa ver a cidade e avaliar as forças da Cruzada Escarlate. Então fale novamente com Kirsta Sombrafunda nas Terras Pestilentas Ocidentais.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Examine Amparo de um ponto alto",
    [6026] = "Leandro Leonardo quer que você pegue 2 Barras de Tório, 1 Bastão Dourado, 8 Bombas de Alto-explosivo e 8 Gatilhos Instáveis.\n\nVocê terá que encontrar um ferreiro e um engenheiro para esses itens.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Barra de Tório x 2\n - Bastão Dourado x 1\n - Bomba de Alto-explosivo x 8\n - Gatilho Instável x 8",
    [6027] = "Pegue o Livro dos Anciãos e entregue-o para Azore Albamorte na torre de Ethel Rethor.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Livro dos Anciãos x 1",
    [6028] = "Entregue o Relatório de Visteterna à Oficial Argêntea Cândida no Acampamento Ventogelante, que fica nas Terras Pestilentas Ocidentais.",
    [6029] = "Entregue o Relatório de Visteterna ao Oficial Argênteo Garush no Baluarte, nas Terras Pestilentas Ocidentais.",
    [6030] = "Entregue o livro \"Estudos sobre Comunicação com Espíritos\" ao Duque Nicolau Zverenhoff na Capela Esperança da Luz, que fica nas Terras Pestilentas Orientais.",
    [6041] = "Viaje ao Bosque Pestilento, a noroeste da Esperança da Luz. Destrua 8 Estruturas do Flagelo usando o Composto Químico Especial do Leandro na Marca de Detonação plantada dentro de cada construção. Os Zigurates e Abatedouros foram marcados por instrução de Leandro.\n\nAssim que você plantar a bomba, deixe a estrutura e contemple seu trabalho.\n\nA propósito, o Composto Químico Especial do Leandro é bastante... instável.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Estruturas do Flagelo destruídas x 8",
    [6042] = "Mate 20 Quirópragos Nóxios e 10 Quirópragos Monstruosos. Fale novamente com Nathanos Arauto da Praga quando você tiver terminado.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Quiróprago Nóxio x 20\n - Quiróprago Monstruoso x 10",
    [6133] = "Vá à fronteira norte das Terras Pestilentas Orientais e recupere os Arquivos de Quel'Thalas em algum lugar do Abrigo Quel'Lithien.\n\nVocê também precisa matar 8 Patrulheiros, 8 Trilheiros e 8 Mateiros.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Trilheiro x 8\n - Patrulheiro x 8\n - Mateiro x 8\n - Arquivos de Quel'Thalas x 1",
    [6134] = "Leve 8 Fantasmoplasmas e o Caixote de Ímãs Fantasmais para Guido Zumbidorla na Desolação.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Fantasmoplasma x 8",
    [6135] = "Encontre Quiroscuro e mate-o. Do cadáver, extraia um Tufo de Pele de Quiroscuro e leve para Nathanos Arauto da Praga.\n\nDizem que Quiroscuro vaga pelo campo ao norte do Sítio dos Marris.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Tufo de Pele de Quiroscuro x 1",
    [6136] = "Encontre Brocassangre nos ermos do norte das Terras Pestilentas e destrua-o. Fale novamente com Nathanos Arauto da Praga quando tiver cumprido a missão.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Brocassangre x 1",
    [6141] = "Fale com o Irmão Anton no Posto do Nijel, que fica em Desolação.",
    [6142] = "Encontre 10 nacos de Carne de Marisco de Casca Mole e entregue-os para Mai'Lahii na Aldeia Pescassombra.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Carne de Marisco de Casca Mole x 10",
    [6143] = "Mate 7 Mirmidões Sibilâmina, 7 Nagas Sibilâmina e 5 Feiticeiras Sibilâmina. Em seguida, fale com Drulzeger, na Aldeia Pescassombra, em Desolação.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Mirmidão Sibilâmina x 7\n - Naga Sibilâmina x 7\n - Feiticeira Sibilâmina x 5",
    [6162] = "Mate o Suserano Ror e leve a garra dele para Dreka'Sur no Posto Peçonha, na Selva Maleva.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Garra do Suserano Ror x 1",
    [6164] = "Get Augustus' Receipt Book from the inn in Terrordale.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Livro-caixa do Augustus x 1",
    [6182] = "Fale com Mathias Shaw na Cidade Velha de Ventobravo. Ele mora no Quartel.",
    [6183] = "Fale com Mathias Shaw novamente se desejar aceitar esta tarefa.",
    [6184] = "Vá até o Acampamento Ventogelante, nas Terras Pestilentas Ocidentais, e encontre seu contato, Flint Umbramais.",
    [6185] = "Vasculhe as Terras Pestilentas Orientais em busca de pistas sobre o \"Arauto da Praga\" e os agentes da AVIN desaparecidos. Ser encontrar alguma Insígnia da AVIN, leve de volta para Flint Umbramais, no Acampamento Ventogelante.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Insígnia SI:7 (Rutger) x 1\n - Insígnia SI:7 (Fredo) x 1\n - Insígnia SI:7 (Turyen) x 1\n - O Arauto da Praga descoberto",
    [6186] = "Return to Stormwind and inform King Varian Wrynn of the fate of Nathanos Marris.",
    [6187] = "Reúna um exército e siga até as Terras Pestilentas Orientais. Lance um ataque maciço contra Nathanos Arauto da Praga e qualquer lixo da Horda que tente protegê-lo.\n\nMantenha-se sempre alerta, Tomcatdk. Os membros da Horda vão defender o lorde patrulheiro com as próprias vidas.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Nathanos Arauto da Praga x 1",
    [6389] = "Solte os Cupins das Terras Pestilentas na Serraria de Beiranorte, depois retorne com o Barril de Cupins das Terras Pestilentas até Nataniel Dumah, no Acampamento Ventogelante, que fica nas Terras Pestilentas Ocidentais.",
    [6390] = "Solte os Cupins das Terras Pestilentas na Serraria de Beiranorte, depois retorne com o Barril de Cupins das Terras Pestilentas até Guel Levine, no Baluarte, que fica nas Clareiras de Tirisfal.",
    [6622] = "Use Bandagens de Triagem para salvar pacientes. Você precisa salvar 15 deles antes que 6 morram devido aos ferimentos.\n\nLembre-se da ordem da triagem! Os Soldados Mortalmente Feridos são os que morrem mais rápido, seguidos dos Soldados Gravemente Feridos. Os Soldados Feridos demoram mais a morrer e devem ser tratados após os outros terem passado pela triagem.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - 15 pacientes salvos!",
    [6624] = "Use Bandagens de Triagem para salvar pacientes. Você precisa salvar 15 deles antes que 6 morram devido aos ferimentos.\n\nLembre-se da ordem da triagem! Os Soldados Mortalmente Feridos são os que morrem mais rápido, seguidos dos Soldados Gravemente Feridos. Os Soldados Feridos demoram mais a morrer e devem ser tratados após os outros terem passado pela triagem.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - 15 pacientes salvos!",
    [6761] = "Volte a Darnassus e fale com Mathrengyl Ursivagus no Enclave Cenariano, em Darnassus.",
    [6762] = "Fale com Ravine Saturna no Refúgio Noturno, que fica na Clareira da Lua. A Clareira da Lua fica entre a Selva Maleva e Hibérnia. Uma estrada que sai do Domínio dos Presamatos leva até lá.",
    [6844] = "Leve o Objeto Silitídeo Incrustado para Umbero, arquivista do Círculo Cenariano, na vila de Refúgio Noturno, na Clareira da Lua.",
    [6845] = "Fale com Ravine Saturna no vilarejo de Refúgio Noturno, na Clareira da Lua.",
    [7003] = "Use o Ultra-encolhedor de Zorbin para deixar qualquer tipo de gigante de Feralas com um tamanho mais compacto e manejável. Colete 15 Resíduos de Miniaturização encontrados nos gigantes encolhidos e leve para Zorbin Cegasvista, que fica nas docas da Costa Esquecida, em Feralas.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Resíduo de Miniaturização x 15",
    [7492] = "Fale com Talo Casco de Espinhos na Aldeia Mojache, em Feralas.",
    [7601] = "Fale com Diabretim na Selva Maleva.",
    [7701] = "Nas profundezas do Fosso de Lava do Caldeirão, na Garganta Abrasadora, você encontrará o anão traidor dos Ferro Negro chamado Feitor Maltorius.\n\nMate o Feitor Maltorius, arranque a cabeça dele e leve-a para o Capitão-vigia Lolo Longalcance na Garganta Abrasadora para receber sua recompensa.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Cabeça do Feitor Maltorius x 1",
    [7702] = "Lá no fundo do Fosso de Lava do Caldeirão, você vai encontrar os dormitórios dos anões Ferro Negro.\n\nVá até lá e roube 20 Travesseiros de Ferro Negro. Fale novamente com Evonice Fumarenta na Garganta Abrasadora quando tiver terminado a tarefa.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Travesseiro de Ferro Negro x 20",
    [7721] = "Colete 10 Âmagos dos Elementais de Água dos Elementais do Mar e dos Borrifos do Mar ao longo da Costa Esquecida de Feralas, e leve-os para Zorbin Cegasvista, próximo às docas.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Âmago do Elemental de Água x 10",
    [7722] = "Em algum lugar do Fosso de Lava do Caldeirão você encontra as Instruções Secretas: Fluxo Ígneo que o Feitor Maltorius roubou. Encontre as instruções e devolva-as ao Mestre Ferreiro Queimatar na Garganta Abrasadora.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Instruções Secretas: Fluxo Ígneo x 1",
    [7723] = "Hansel Mão-pesada quer que você mate 20 Golens de Guerra Pesados.\n\nOs Golens de Guerra Pesados perambulam pelas áreas em torno do Caldeirão, na Garganta Abrasadora. Quando tiver matado a quantidade necessária, fale novamente com Hansel na Garganta Abrasadora.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Golem de Guerra Pesado x 20",
    [7724] = "Hansel Mão-pesada quer que você mate 20 Grandes Aranhas de Lava na Garganta Abrasadora. Você pode encontrá-las nos platôs e poços de lava do local.\n\nDepois de matar as Grandes Aranhas de Lava, fale novamente com Hansel Mão-pesada na Garganta Abrasadora.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Grande Aranha de Lava x 20",
    [7727] = "Hansel Mão-pesada quer que você mate 20 Incendossauros.\n\nEles ocupam as poças dentro do Fosso de Lava. O Fosso de Lava fica no Caldeirão da Garganta Abrasadora.\n\nDepois de matar os Incendossauros, fale novamente com Hansel Mão-pesada na Garganta Abrasadora.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Incendossauro x 20",
    [7728] = "Encontre e devolva o Alcaraviz de Ferraria e a Luneta do Espia ao Capataz Scrange na Garganta Abrasadora.\n\nA única informação que você tem sobre esses itens é a seguinte: com certeza foram roubados por anões Ferro Negro. O Alcaraviz de Ferraria é uma ferramenta usada por ferreiros e a Luneta do Espia é um aparelho de monitoramento inestimável para vigias. \n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Alcaraviz de Ferraria x 1\n - Luneta do Espia x 1",
    [7729] = "Mate 15 Capatazes Ferro Negro e 15 Feitores Ferro Negro. Depois, fale novamente com Capataz Scrange, na Garganta Abrasadora.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Capataz Ferro Negro x 15\n - Feitor Ferro Negro x 15",
    [7730] = "Traga 20 Carapaças de Zukk'ash para Hadoken Passo Veloz na Aldeia Mojache, que fica em Feralas.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Carapaça de Zukk'ash x 20",
    [7731] = "Derrote Cravaçoite na Profundeza Atormentada, leve as glândulas para Hadoken Passo Veloz na Aldeia Mojache, que fica em Feralas.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Glândulas de Cravaçoite x 1",
    [7732] = "Entregue o Relatório sobre Zukk'ash da Aldeia Mojache para Zilzibin Tamborleiro. Ele fica no Bazar de Orgrimmar.",
    [7733] = "Leve 10 Pelegos de Yeti Cortifúria para Pradal McGrubben no Domínio de Plumaluna, em Feralas.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Pelego de Yeti Cortifúria x 10",
    [7734] = "Leve 10 Pelegos de Yeti Cortifúria para Jangdor Passo Veloz na Aldeia Mojache, em Feralas.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Pelego de Yeti Cortifúria x 10",
    [7735] = "Leve o Pelego Imaculado de Yeti para Pradal McGrubben no Domínio de Plumaluna, em Feralas.",
    [7738] = "Leve o Pelego Perfeito de Yeti para Jangdor Passo Veloz na Aldeia Mojache, que fica em Feralas.",
    [7815] = "Katoom the Angler at Revantusk Village in the Hinterlands wants you to kill 10 Saltwater Snapjaw turtles. Return to him when you have completed this task.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Mordelisca de Água Salgada x 10",
    [7816] = "Katum, o Pesqueiro, na Aldeia Revatusco, que fica nas Terras Agrestes, quer que você mate Gammerita e apanhe a Melhor Isca de Katum da carcaça.\n\nKatum mencionou que Gammerita circula pela costa com as outras tartarugas.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Melhor Isca de Katum x 1",
    [7817] = "|Cffffd100Requisitos:|r\n - Magitrama x 60",
    [7828] = "Huntsman Markhor at Revantusk Village in the Hinterlands wants you to kill 5 Silvermane Stalkers and 5 Silvermane Howlers. Return to him once the task is complete.\n\nMarkhor mentioned that the wolves hide in the wilds of the Hinterlands.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Espreitador Jubaprata x 5\n - Uivador Jubaprata x 5",
    [7829] = "Huntsman Markhor at Revantusk Village in the Hinterlands wants you to kill 10 Savage Owlbeasts. Return to him once the task is complete.\n\nMarkhor mentioned that the Savage Owlbeasts occupy the wilds of the Hinterlands.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Feruja Selvagem x 10",
    [7830] = "O Guarda-caça Markhor da Aldeia Revatusco, que fica nas Terras Agrestes, quer que você mate um Senhor Celeste Bicavalha e tire dele a Pluma de Senhor Celeste. Ao terminar a tarefa, fale novamente com Markhor.\n\nEle mencionou que os Senhores Celestes Bicavalha são uma espécie rara que ocupa a selva nas Terras Agrestes.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Pluma de Senhor Celeste x 1",
    [7839] = "O Ferreiro Escoriárvore, da Aldeia Revatusco, que fica nas Terras Agrestes, quer que você encontre as Ferramentas Perdidas de Escoriárvore. Quando terminar, fale novamente com ele.\n\nVocê lembra que Escoriárvore mencionou que as ferramentas podem estar em um dos templos Ramatorpe no nordeste das Terras Agrestes. É bom procurar também em Rocha Oculta.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Ferramentas Perdidas de Escoriárvore x 1",
    [7840] = "Banha na Aldeia Revatusco, que fica nas Terras Agrestes, quer que você encontre o Almoço do Banha. Fale novamente com ele quando a tarefa for concluída.\n\nBanha mencionou tê-lo esquecido na ilha ao norte. Cuidado com os trolls Ramatorpe.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Almoço do Banha x 1",
    [7841] = "Otho Moji'ko at Revantusk Village in the Hinterlands wants you to slaughter 8 Highvale Scouts, 5 Highvale Outrunners, 5 Highvale Rangers and 2 Highvale Marksman. Return to him when this task is complete.\n\nYou can find the Highvale high elves at the Quel'Danil Lodge in the northwestern region of the Hinterlands.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Batedor do Altovale x 8\n - Vanguardeiro do Altovale x 5\n - Patrulheiro do Altovale x 5\n - Atirador Perito do Altovale x 2",
    [7842] = "Otho Moji'ko at Revantusk Village in the Hinterlands wants you to bring him 1 Long Elegant Feather from the gryphons that inhabit the Hinterlands. Return to him once this task is complete.\n\nGryphons are known to inhabit every region of the Hinterlands.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Pena Longa Elegante x 1",
    [7843] = "Joho'sér Mujik'ka da Aldeia Revatusco, que fica nas Terras Agrestes, quer que você viaje para o Ninho da Águia, na região oeste das Terras Agrestes, e deixe a Mensagem Final para os Martelo Feroz ao lado do poço no centro da cidade. Quando terminar, fale novamente com ele.\n\nCuidado com anões Martelo Feroz furiosos e com membros irritados da Aliança.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Mensagem para os Martelo Feroz entregue",
    [7844] = "Mystic Yayo'jin at Revantusk Village in the Hinterlands wants you to kill 6 Vilebranch Scalpers and 2 Vilebranch Soothsayers. Return to her when this task is complete.\n\nYayo'jin indicated that these trolls could be found near the Shaol'watha and Agol'watha temples in the north by northeastern region of the Hinterlands.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Escalpelador Ramatorpe x 6\n - Adivinho Ramatorpe x 2",
    [8151] = "Encontre Ogtinc em Azshara.",
    [8153] = "Leve um par de Galhadas Perfeitas de Ginete para Ogtinc em Azshara. Ogtinc vive no alto dos penhascos a nordeste das Ruínas de Eldarath.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Galhada Perfeita de Ginete x 2",
    [8231] = "Leve 6 Escamas de Fura-ondas para Ogtinc em Azshara. Ogtinc mora no alto dos penhascos a nordeste das Ruínas de Eldarath.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Escamas de Fura-ondas x 6",
    [8250] = "Procure Sanath Lim-yo e consiga o transporte para ver o Arquimago Tauriel.",
    [8251] = "Leve 10 Poeiras Reluzentes para o Arquimago Tauriel.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Pó Reluzente x 10",
    [8252] = "Obtenha 6 Corais Encantados e entregue-os ao Arquimago Tauriel.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Coral Encantado x 6",
    [8254] = "Encontre Ogtinc em Azshara.",
    [8255] = "Obtenha 4 Glândulas de Ginete Saudável leve-as para Ogtinc em Azshara. Ogtinc vive no alto dos penhascos a nordeste das Ruínas de Eldarath.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Glândula de Ginete Saudável x 4",
    [8256] = "Obtenha uma Linfa de Morte-viva para Ogtinc em Azshara. Ogtinc mora no alto dos penhascos a nordeste das Ruínas de Eldarath.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Linfa de Morte-viva x 1",
    [8275] = "Apresente-se à Arauto do Vento Chifre Altivo no Forte Cenariano, em Silithus.",
    [8276] = "Apresente-se à Arauto do Vento Chifre Altivo no Forte Cenariano, em Silithus.",
    [8277] = "Belhatriz Corredente, no Forte Cenariano em Silithus, quer que você traga 8 Aguilhões de Escorpídeo Petraçoite e 8 Presas de Rastejante da Areia. Fale com ela quando tiver completado a tarefa.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Aguilhão de Escorpídeo Petraçoite x 8\n - Presa de Rastejante da Areia x 8",
    [8278] = "Belhatriz Corredente, no Forte Cenariano em Silithus, quer que você consiga 3 Aguilhões de Tenaz Petraçoite, 3 Aguilhões de Esfolador Petraçoite e 3 Presas de Espreitadora de Rocha.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Aguilhão de Esfolador Petraçoite x 3\n - Aguilhão de Tenaz Petraçoite x 3\n - Presa de Espreitadora de Rocha x 3",
    [8279] = "Traga os três capítulos do Dicionário do Crepúsculo para o Eremita Ortell em Silithus.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Dicionário do Crepúsculo – Capítulo 1 x 1\n - Dicionário do Crepúsculo – Capítulo 2 x 1\n - Dicionário do Crepúsculo – Capítulo 3 x 1",
    [8280] = "O Arauto do Vento Chifre Altivo, no Forte Cenariano, quer que você mate 15 Dragatacas.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Dragataca x 15",
    [8281] = "O Arauto do Vento Chifre Altivo, no Forte Cenariano, quer que você mate 20 Dragasmagas.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Dragasmaga x 20",
    [8282] = "Vá para as montanhas na parte sul de Silithus e recupere a Algibeira do Noggel. Devolva-a no Forte Cenariano.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Algibeira do Noggel x 1",
    [8283] = "Leve a Tenaz da Agarramata para Vish Kozus, Capitão da Guarda.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Tenaz da Agarramata x 1",
    [8284] = "A Geóloga Matarriso, no Forte Cenariano, quer que você recolha 8 Fragmentos de Tabuleta do Crepúsculo.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Fragmento de Tabuleta do Crepúsculo x 8",
    [8285] = "Leve a tabuleta restaurada para o Eremita Ortell em Silithus.",
    [8287] = "Leve a Transcrição Decodificada da Tabuleta para o Comandante Mar'alith.",
    [8304] = "O Comandante Mar'alith, no Forte Cenariano, em Silithus, quer que você interrogue os ocupantes do Acampamento Barbabronze. Ele fica diretamente ao sul do Forte Cenariano.\n\nUma vez obtidas as informações solicitadas, fale novamente com o Comandante Mar'alith.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Frankal questionado x 1\n - Rutgar questionado x 1",
    [8308] = "Leve a Carta de Brann Barbabronze para o Acampamento do Barbabronze na região sul de Silithus.",
    [8318] = "Traga 10 Textos Cifrados do Crepúsculo para Bor Juba Agreste no Forte Cenariano.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Texto Codificado do Crepúsculo x 10",
    [8320] = "Mate 10 Geolordes do Crepúsculo, então fale novamente com Huum Juba Agreste no Forte Cenariano.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Geolorde do Crepúsculo x 10",
    [8323] = "O Eremita Ortell quer que você traga 10 Textos Codificados do Crepúsculo\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Texto Codificado do Crepúsculo x 10",
    [8361] = "Bor Juba Agreste no Forte Cenariano, em Silithus, quer que você acabe com um Templário Abissal e leve um Escudo Abissal para ele como prova.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Escudo Abissal x 1",
    [8365] = "Sissi Ashando quer que você colete 20 Chapéus de Pirata dos Mares do Sul e leve-os para ela no Porto de Bondebico, em Tanaris.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Chapéu de Pirata dos Mares do Sul x 20",
    [8366] = "O Chefe de Segurança Silvadágua do Porto de Bondebico, em Tanaris, quer que você mate 10 Piratas dos Mares do Sul, 10 Flibusteiros dos Mares do Sul, 10 Estivadores dos Mares do Sul e 10 Espadachins dos Mares do Sul.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Pirata dos Mares do Sul x 10\n - Flibusteiro dos Mares do Sul x 10\n - Estivador dos Mares do Sul x 10\n - Espadachim dos Mares do Sul x 10",
    [8410] = "Colete uma amostra de ar, fogo, terra e água para Bath'rah, o Vigia dos Ventos.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Ar Elemental x 1\n - Fogo Elemental x 1\n - Terra Elemental x 1\n - Água Elemental x 1",
    [8412] = "Leve as partes para Bath'rah, o Vigia dos Ventos, fazer o totem de espírito.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Olho Injetado de Aranha x 8\n - Garra Preta Maciça x 8",
    [8413] = "Leve as Penas Vodu para Bath'rah, o Vigia dos Ventos.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Pena Vodu Âmbar x 2\n - Pena Vodu Azul x 2\n - Pena Vodu Verde x 2",
    [8414] = "Colete 20 Pedras do Flagelo do Lacaio e leve-as ao Sumo Sacerdote Thel'danis nas Terras Pestilentas Ocidentais.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Pedra do Flagelo do Lacaio x 20",
    [8415] = "Fale com o Comandante Ashlam Punhobom no Acampamento Ventogelante.",
    [8416] = "Leve a Pedra do Flagelo Inerte ao Comandante Ashlam Punhobom no Morro do Ventogelante.",
    [8417] = "Fale com o Herói Caído da Horda na entrada da Barreira do Inferno.",
    [8418] = "Collect Amber, Blue, and Green Voodoo Feathers from the trolls of Sunken Temple.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Pena Vodu Âmbar x 2\n - Pena Vodu Azul x 2\n - Pena Vodu Verde x 2",
    [8419] = "Leve um pedaço de Tecido Vil para Diabretim, em Selva Maleva.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Tecido Vil x 1",
    [8421] = "Leve 10 pedaços de Madeira Podre e 4 Essências de Peçonha para Diabretim em Selva Maleva.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Madeira Podre x 10\n - Essência de Peçonha x 4",
    [8423] = "Mate 7 Javiltuscos na Barreira do Inferno e fale novamente com o Herói Caído da Horda.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Javiltusco x 7",
    [8424] = "Mate os Devotos da Sombra na Barreira do Inferno e fale novamente com o Herói Caído da Horda.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Adepto Devoto da Sombra x 20\n - Sectário Devoto da Sombra x 10\n - Capanga Devoto da Sombra x 20",
    [8460] = "Grazle quer que você prove seu valor matando 6 Guerreiros de Lenha Morta, 6 Desbravadores de Lenha Morta e 6 Jardineiros de Lenha Morta. Quando você terminar, fale novamente com ele na parte sul da Selva Maleva, perto do Santuário Esmeralda.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Guerreiro de Lenha Morta x 6\n - Desbravador de Lenha Morta x 6\n - Jardineiro de Lenha Morta x 6",
    [8461] = "Nafien quer que você mate 6 Vigias do Covil de Lenha Morta, 6 Vingadores de Lenha Morta e 6 Xamãs de Lenha Morta. Fale novamente com ele na parte norte da Selva Maleva, perto da entrada do Domínio dos Presamatos.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Vigia do Covil de Lenha Morta x 6\n - Vingador de Lenha Morta x 6\n - Xamã de Lenha Morta x 6",
    [8462] = "Viaje para o norte ao longo da estrada principal em Selva Maleva e fale com o pelurso chamado Nafien. Ele monta guarda diante da entrada do Domínio dos Presamatos.",
    [8464] = "Salfa quer que você mate 8 Xamãs Invernosos, 8 Vigias do Covil dos Invernosos e 8 Ursinos Invernosos. Salfa está perto da entrada do Domínio dos Presamatos, em Hibérnia.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Xamã Invernoso x 8\n - Vigia do Covil dos Invernosos x 8\n - Ursino Invernoso x 8",
    [8465] = "Viaje pelo Domínio dos Presamatos até chegar a Hibérnia. Fale com Salfa, que está de guarda em frente à entrada do Domínio dos Presamatos.",
    [8467] = "|Cffffd100Requisitos:|r\n - Pena do Cocar de Lenha Morta x 5",
    [8551] = "Leve o Baú do Smotts para Hacklebury Smotts na Angra do Butim.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Baú do Smotts x 1",
    [9051] = "Espete um Demossauro com uma Farpa para Demossauro. Fale novamente com Torwa Mateiro na Cratera Un'goro depois que tiver terminado a tarefa.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Uma farpa no demossauro",
    [9052] = "Colete 8 Cogumelos Vermelhos e 8 Ferrões de Gorishi e fale novamente com Torwa Mateiro na Cratera Un'Goro.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Ferrão de Gorishi x 8\n - Cogumelo Vermelho x 8",
    [9063] = "Fale com Torwa Mateiro na Cratera Un'Goro.",
    [9124] = "O Guarda-caça Leopold, na Capela Esperança da Luz, que fica nas Terras Pestilentas Orientais, quer 30 Pedaços de Carnífice da Cripta.\n|Cffffd100 |r\n|Cffffd100Requisitos:|r\n - Pedaços de Carnífice da Cripta x 30",
