    [4984] = "Убейте 8 больных волков и вернитесь к Мулгрису Глубокой Реке в Удел Страданий в Западных Чумных землях.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Больной волк x 8",
    [4985] = "Убейте 8 болезненных гризли и вернитесь к Мулгрису Глубокой Реке в Удел Страданий в Западных Чумных землях.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Болезненный гризли x 8",
    [4986] = "Судя по магии, вложенной в покрытую письменами дубовую ветвь, друид-таурен желал отнести ее в Круг Кенария в Дарнасе. Попросите о помощи одного из тамошних друидов.",
    [4987] = "Судя по магии, записанной в покрытую письменами дубовую ветвь, друид-таурен желал отнести ее в Круг Кенария в Громовом Утесе. Попросите о помощи одного из тамошних друидов.",
    [5021] = "Судя по сбивчивым объяснениям призрачной женщины, она хочет доставить посылку. Бедняга уверяет, что посылка лежала там, где стоял ее конь. Где именно это было, женщина не сказала.",
    [5022] = "Выясните у королевских представителей Переписи Штормграда местонахождение Эммы Фелстоун. Офицера-переписчика можно найти в Ратуше.",
    [5023] = "Поговорите с королевскими инспекторами Подгорода и наведите справки о Иеремии Фелстоуне. Офицера-переписчика можно найти рядом с пунктом регистрации гильдий и гербовых накидок.",
    [5048] = "Найдите в Штормграде старушку Эмму и узнайте, действительно ли она – Эмма Фелстоун. Если да, то, может, ей понравится та посылочка, что собрала для нее Джанис Фелстоун.",
    [5049] = "Найдите в Подгороде Иеремию Пейсона и выясните, в самом ли деле он Иеремия Фелстоун. Если да, передайте ему посылку от Джанис Фелстоун.",
    [5050] = "Отнесите половинку талисмана Джанис Фелстоун в Западные Чумные земли.",
    [5051] = "Отыщите вторую половину оберега Удачи на ферме Фелстоун в Западных Чумных землях и соберите его.\n\nОтдайте оберег Удачи Джанис Фелстоун.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Оберег удачи x 1",
    [5054] = "По просьбе Шторма Призрачное Копыто в Круговзоре в Зимних Ключах убейте Урсиуса.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Урсиус x 1",
    [5055] = "Убейте Брумеран по просьбе Шторма Призрачное Копыто в Круговзоре в Зимних Ключах.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Брумеран x 1",
    [5060] = "|Cffffd100Требования:|r\n - Ключ от шкафа Далсонов x 1",
    [5066] = "Найдите командира Ашлама Отважного Кулака в лагере Промозглого Ветра на севере Альтеракских гор.",
    [5082] = "По просьбе Доновы Снегоноры из Зимних Ключей убейте 8 землепроходцев, 8 дозорных и 8 тотемистов из племени Зимней Спячки.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Землепроходец из племени Зимней Спячки x 8\n - Дозорный из племени Зимней Спячки x 8\n - Тотемист из племени Зимней Спячки x 8",
