    [5510] = "|Cffffd100Требования:|r\n - Камень приспешника Плети x 20",
    [5527] = "Отправляйтесь в Силитус и найдите реликварий чистоты в руинах деревни Южного Ветра. Если вам удастся его найти, возвращайтесь к Рабину Сатурне в Ночную Гавань на Лунной поляне.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Реликварий Чистоты x 1",
    [5533] = "Поговорите с алхимиком Арбингтоном с вершины Холодной Скалы, что в Западных Чумных землях.",
    [5534] = "Найдите некую руну, которую хранят наги в руинах Эльдарата и принесите ее Ким'джаелю в Азшару.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Некая руна x 1",
    [5535] = "Уничтожьте по 6 теней высокорожденных обоих полов и возвращайтесь к Ло'хату в Азшару.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Тень высокорожденной x 6\n - Призрак высокорожденного x 6",
    [5536] = "Уничтожьте 6 сатиров, 2 плутов Халдарра и 2 скверноподданных из племени Халдарр и возвращайтесь к Ло'хату к границе Ясеневого леса.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Сатир из племени Халдарр x 6\n - Плут из племени Халдарр x 2\n - Скверноподданный из племени Халдарр x 2",
    [5537] = "Принесите 15 фрагментов скелетов алхимику Арбингтону в Берег Промозглого Ветра в Западных Чумных землях.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Фрагменты скелетов x 15",
    [5542] = "Убейте 20 карликовых чумных псов, 5 чумных псов и 5 бесноватых чумных псов. По выполнении задания вернитесь к Тириону Фордрингу.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Карликовый чумной пес x 20\n - Чумной пес x 5\n - Бесноватый чумной пес x 5",
    [5543] = "Убейте 30 чумных нетопырей и вернитесь к Тириону Фордрингу.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Чумной нетопырь x 30",
    [5544] = "Найдите личинок-падальщиков и падальщиков-пожирателей. Убейте побольше и наберите их мяса. Возвращайтесь к Тириону Фордрингу, когда насобираете 15 кусков мяса червей-трупоедов.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Кусок мяса червя-трупоеда x 15",
    [5581] = "Уничтожьте 6 порталов на поле Маннорок, а затем вернитесь к Тайге Мудрой Гриве в деревню Ночных Охотников.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Порталы закрыты x 6",
    [5601] = "Найдите Памелу Редпат в Дарроушире.",
    [5655] = "Поговорите с Ур'кьо в Оргриммаре.",
    [5742] = "Выслушайте Тириона Фордринга.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - История Тириона",
    [5781] = "Отправляйтесь в Крипту – в самой южной части Восточных Чумных земель – и добудьте молот Телана. Отнесите его Тириону Фордрингу.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Молот Телана x 1",
    [5845] = "Отправляйтесь в Северный дол, что в северо-восточной части Восточных Чумных земель и найдите там символ утраченной чести. После этого вернитесь к Тириону Фордрингу.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Символ утраченной чести x 1",
    [5846] = "Отправляйтесь на остров Каэр Дарроу в центральной части Чумных земель и поищите там картину.",
    [5863] = "Анди Линн из Прибамбасска просит уничтожить поселение Песчаного Молота, убив 10 громил и 10 мародеров из клана Песчаного Молота и Гор'марока Опустошителя.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Громила из клана Песчаного Молота x 10\n - Головорез из клана Песчаного Молота x 10\n - Гор'марок Опустошитель x 1",
    [5882] = "|Cffffd100Требования:|r\n - Оскверненный осколок души x 6",
    [5901] = "Возьмите пустую банку для термитов и отправляйтесь к большим термитникам в Восточных Чумных землях. Наберите 100 термитов Восточных Чумных земель и возвращайтесь к Микки Левину в Бастион в Тирисфальских лесах.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Термиты Чумных земель x 100",
    [5902] = "Найдите подходящее место в центре лесопилки Северного Кряжа в Западных Чумных землях и оставьте там бочонок термитов из Чумных земель.",
    [5903] = "Возьмите пустую банку для термитов и отправляйтесь к большим термитникам в Восточных Чумных землях. Наберите 100 термитов Восточных Чумных земель и возвращайтесь к Натаниэлю Дамаху в Лагерь Промозглого Ветра в Восточные Чумные земли.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Термиты Чумных земель x 100",
    [5904] = "Найдите подходящее место в центре лесопилки Северного Кряжа в Западных Чумных землях и оставьте там бочонок термитов из Чумных земель.",
    [5943] = "Проводите караван Гизлтона через Поле Маннорок. О награде поговорите со Смидом: его можно найти в Лагере Заржавня.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Проводите караван Гизлтона через Поле Маннорок.",
    [6004] = "Убейте 2 медиков, 2 охотников, 2 волхвов и 2 рыцарей из Алого ордена и вернитесь к Кристе Чернотени в Западных Чумных землях.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Медик Алого ордена x 2\n - Охотник из Алого ордена x 2\n - Маг Алого ордена x 2\n - Рыцарь Алого ордена x 2",
    [6021] = "Принесите голову Зельдарра Управляющему Алену в Часовню Последней Надежды.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Голова Зельдарра x 1",
    [6022] = "Отправляйтесь на восток в Перекресток Корина и убейте несколько населяющих его созданий Плети. Соберите 7 кусков живой гнили менее, чем за 10 минут. С помощью ступки и пестика превратите живую гниль в сгущенную гниль.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Сгущенная гниль x 1",
    [6023] = "Убейте охотника Радли и кавалериста Даргена и вернитесь к Кристе Чернотени в Западные Чумные земли.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Охотник Радли x 1\n - Кавалер Алого ордена Дарген x 1",
    [6024] = "Убейте секретного агента Хамейю. С помощью ключа извлеките сокровище из кучи грязи за Криптой.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Ключ Хамейи x 1",
    [6025] = "Проберитесь в Дольный Очаг и найдите возвышенность, с которой открывается вид на весь город. Оцените силы Алого ордена и вернитесь к Кристе Чернотени в Западных Чумных землях.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Осмотреть Дольный Очаг с высоты",
    [6026] = "Принесите 2 ториевых слитка, 1 золотой жезл, 8 высоковзрывчатых бомб и 8 нестабильных детонаторов Смоки Ля Рю.\n\nДля этого вам придется найти кузнеца и инженера.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Ториевый слиток x 2\n - Золотой жезл x 1\n - Взрывоопасная бомба x 8\n - Нестабильное пусковое устройство x 8",
    [6027] = "Отыщите Книгу Древних и принесите ее Азору Альдаморту в башню Этель-Ретор.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Книга Древних x 1",
    [6028] = "Доставьте донесение из Круговзора офицеру Чистосердой из ордена Серебряного Рассвета в Лагерь Промозглого Ветра в Западных Чумных землях.",
    [6029] = "Доставьте донесение из Круговзора офицеру Гарушу из ордена Серебряного Рассвета в Бастион в Западных Чумных землях.",
    [6030] = "Доставьте книгу \"Исследования речи духов\" Герцогу Николасу Зверенхоффу в Часовню Последней Надежды в Восточных Чумных землях.",
    [6041] = "Отправляйтесь в Проклятый лес, к северо-западу от Часовни Последней Надежды. Уничтожьте 8 сооружений Плети, отмеченных знаком взрыва, поместив особый состав Дымка внутрь сооружения. Дымок отметил зиккураты и бойни.\n\nРазместив бомбу внутри сооружения, немедленно выбирайтесь наружу и любуйтесь делом рук своих с безопасного расстояния. \n\nАх да, вот еще что, особый состав Дымка – вещество весьма нестабильное.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Уничтожено сооружений Плети x 8",
    [6042] = "Истребите 20 пагубных нетопырей и 10 чудовищных чумных нетопырей. После возвращайтесь к Натаносу Гнилостеню.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Пагубный нетопырь x 20\n - Чудовищный чумной нетопырь x 10",
    [6133] = "Отправляйтесь в северные пределы Восточных Чумных земель и заполучите реестр Кель'Таласа. Документ находится где-то в Сторожке Кель'Литиен.\n\nТакже убейте 8 следопытов, 8 разведчиц дорог и 8 лесников.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Разведчица дорог x 8\n - Следопыт x 8\n - Лесник x 8\n - Реестр Кель'Таласа x 1",
    [6134] = "Принесите 8 единиц эктоплазмы и ящик призрачных магнитов Хормицу Жужжалиту в Пустоши.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Эктоплазма x 8",
    [6135] = "Выследите Тенекрыла и убейте его. Принесите полоску меха Тенекрыла Натаносу Гнилостеню.\n\nГоворят, Тенекрыл рыщет в землях к северу от имения Марриса.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Полоска меха Тенекрыла x 1",
    [6136] = "Найдите и уничтожьте Мерзкослиза в северных пустошах Восточных Чумных земель. Возвращайтесь к Натаносу Гнилостеню по выполнению задания.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Мерзкослиз x 1",
    [6141] = "Поговорите с братом Антоном на Высоте Найджела, что в Пустошах.",
    [6142] = "Найдите 10 кусочков мяса мягкокожего моллюска и принесите Май'Лахии в деревню Ночных Охотников.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Мясо мягкокожего моллюска x 10",
    [6143] = "Убейте 7 мирмидонов, 7 наг и 5 колдуний из клана Скользящего Плавника, а затем вернитесь в деревню Ночных Охотников и поговорите с Друлзегаром.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Мирмидон из клана Скользящего Плавника x 7\n - Нага из клана Скользящего Плавника x 7\n - Колдунья из клана Скользящего Плавника x 5",
    [6162] = "Убейте властителя Рора и принесите его коготь Дрека'Сур на заставу Отравленной Крови в Оскверненном лесу.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Коготь Властителя Рора x 1",
    [6164] = "Найдите книгу рецептов Августа Помешанного в таверне в Долине Ужаса.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Книга рецептов Августа x 1",
    [6182] = "Поговорите с Матиасом Шоу в Старом Городе в Штормграде. Матиас находится в казармах.",
    [6183] = "Еще раз поговорите с Матиасом Шоу, если хотите принять его задание.",
    [6184] = "Отправляйтесь в Лагерь Промозглого Ветра в Западных Чумных землях и найдите Флинта Тенемора.",
    [6185] = "Поищите в Восточных Чумных землях сведения про некоего Гнилостеня и пропавших агентов ШРУ. Если удастся найти знаки отличия ШРУ, отнесите их Флинту Тенемору в Лагерь Промозглого Ветра.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Знак отличия ШРУ (Рутгер) x 1\n - Знак отличия ШРУ (Фредо) x 1\n - Знак отличия ШРУ (Турьен) x 1\n - Гнилостень выведен на чистую воду",
    [6186] = "Возвращайтесь в Штормград и сообщите королю Вариану Ринну о судьбе Натаноса Марриса.",
    [6187] = "Соберите войско и отправляйтесь в Восточные Чумные земли. Проведите полномасштабную атаку на Натаноса Гнилостеня и любых воинов Орды, которые могут попытаться его защитить.\n\nБудьте осторожны, потому что Орда всеми силами будет защищать бывшего предводителя следопытов.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Натанос Гнилостень x 1",
    [6389] = "Выпустите термитов с Чумных земель на лесопилке Северного Кряжа и возвращайтесь к Натаниэлю Дамаху в Лагерь Промозглого Ветра в Западные Чумные земли.",
    [6390] = "Выпустите термитов Чумных земель на лесопилке Северного Кряжа и возвращайтесь к Микки Левину в Бастион в Тирисфальских лесах.",
    [6622] = "Окажите помощь пациентам, используя для перевязки их ран очередные перевязочные бинты. Вы должны оказать помощь 15 пациентам прежде, чем 6 из них успеют умереть от ран.\n\nПомните о порядке оказания помощи! Находящиеся при смерти солдаты умрут быстрее, чем тяжелораненые. Легкораненые могут прожить дольше всех, потому им надо оказывать помощь ПОСЛЕ того, как перевяжете тех, кто при смерти, и тяжелораненых.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - 15 пациентов спасено!",
    [6624] = "Окажите помощь пациентам, используя для перевязки их ран очередные перевязочные бинты. Вы должны оказать помощь 15 пациентам прежде, чем 6 из них успеют умереть от ран.\n\nПомните о порядке оказания помощи! Находящиеся при смерти солдаты умрут быстрее, чем тяжелораненые. Легкораненые могут прожить дольше всех, потому им надо оказывать помощь ПОСЛЕ того, как перевяжете тех, кто при смерти, и тяжелораненых.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - 15 пациентов спасено!",
    [6761] = "Поговорите с Матренгилом Медвежьей Лапой в анклаве Кенария в Дарнасе.",
    [6762] = "Поговорите с Рабином Сатурной в селении Ночная Гавань на Лунной поляне. Лунная поляна находится между Оскверненным лесом и Зимними Ключами, туда можно добраться по дороге, идущей от крепости Древобрюхов.",
    [6844] = "Отнесите покрытый коркой предмет Умберу – архивариусу Круга Кенария – в деревню Ночная Гавань, что на Лунной поляне.",
    [6845] = "Поговорите с Рабином Сатурной в деревне Ночная Гавань на Лунной поляне.",
    [7003] = "Используйте ультрасжиматель Зорбина, чтобы уменьшить великанов любого вида, каких вы найдете в Фераласе. Принесите 15 миниатюрных великанов этих великанов Зорбину Дребезгуну в порт Забытого берега в Фераласе.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Миниатюрный великан x 15",
    [7492] = "Поговорите с Тало Терновым Копытом в Лагере Мохаче в Фераласе.",
    [7601] = "Поговорите с Бесенком в Оскверненном лесу.",
    [7701] = "Дворф из клана Черного Железа, вероломный надзиратель Мальториус, скрывается в глубине Шлаковой Ямы в Котле, что в Тлеющем ущелье. \n\nУбейте надзирателя Мальториуса и принесите его голову дозорному капитану Лоло Долгобою.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Голова надзирателя Мальториуса x 1",
    [7702] = "Найдите спальные кварталы дворфов Черного Железа в глубине Шлаковой Ямы, что в Котле. \n\nУкрадите 20 подушек дворфов Черного Железа. По выполнении задания вернитесь к Эвонис Сажекурке.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Подушка Черного Железа x 20",
    [7721] = "Соберите 10 ядер элементалей воды, убивая морских элементалей и морских валов на Забытом берегу в Фераласе, и принесите их Зорбину Дребезгуну, который находится неподалеку от порта.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Ядро элементаля воды x 10",
    [7722] = "Верните секретные документы по изготовлению огненного плавня, украденные надзирателем Мальториусом. Инспектор скорее всего находится в Шлаковой Яме в Котле. Документы верните мастеру-кузнецу Гориврату в Тлеющем ущелье.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Секретный рецепт: огненный плавень x 1",
    [7723] = "По поручению Хансела Большерука убейте 20 тяжелых боевых големов, которых можно найти в землях, окружающих Котел в Тлеющем ущелье. По выполнении задания вернитесь к Ханселу.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Тяжелый боевой голем x 20",
    [7724] = "По указанию Хансела Большерука убейте 20 больших лавовых пауков в Тлеющем ущелье. Пауки водятся на вулканических плато и в озерах лавы в Тлеющем ущелье.\n\nкогда убьете достаточно пауков, вернитесь к Ханселу.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Большой лавовый паук x 20",
    [7727] = "По поручению Хансела Большерука убейте 20 пламезавров, обитающих в озерах лавы в Шлаковой Яме - это часть Котла в Тлеющем ущелье. \n\nПо выполнении задания вернитесь к Ханселу в Тлеющее ущелье.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Пламезавр x 20",
    [7728] = "Найдите и верните фурму и подзорную трубу наблюдателя надсмотрщику Скренджу в Тлеющем ущелье. Единственная известная информация: вещи были украдены дворфами Черного Железа. Подзорная труба является бесценным прибором для наблюдения, а фурма – незаменимым кузнечным инструментом.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Фурма x 1\n - Подзорная труба наблюдателя x 1",
    [7729] = "Убейте 15 надсмотрщиков и 15 поработителей из клана Черного Железа. По выполнении задания вернитесь к десятнику Скренджу в Тлеющее ущелье.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Надсмотрщик из клана Черного Железа x 15\n - Поработитель из клана Черного Железа x 15",
    [7730] = "Принесите 20 панцирей Зукк'аш Хадокену Быстрому Страннику в Лагерь Мохаче в Фераласе.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Панцирь Зукк'аш x 20",
    [7731] = "Убейте жалохвоста в Гудящей Бездне и принести железы ядохлеста Хадокену Быстрому Страннику в Лагерь Мохаче в Фераласе.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Железы жалохвоста x 1",
    [7732] = "Доставьте отчет о нашествии насекомых-Зукк'аш на Лагерь Мохаче Зилзибину Барабанному Бою, который проживает на Волоке в Оргриммаре.",
    [7733] = "Принесите 10 шкур йети из пещеры Бешеного оврага Пратту Макгруббену в Крепость Оперенной Луны в Фераласе.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Шкура йети из пещеры Бешеного оврага x 10",
    [7734] = "Принесите 10 шкур йети из пещеры Бешеного оврага Джангору Быстрому Страннику в Лагерь Мохаче в Фераласе.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Шкура йети из пещеры Бешеного оврага x 10",
    [7735] = "Отнесите безупречную шкуру йети Пратту Макгруббену в Крепость Оперенной Луны в Фераласе.",
    [7738] = "Отнесите безупречную шкуру йети Джангору Быстрому Страннику в Лагерь Мохаче в Фераласе.",
    [7815] = "Убейте 10 морских хрустогрызов по поручению Катума Удильщика из деревни Сломанного Клыка во Внутренних землях.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Морской хрустогрыз x 10",
    [7816] = "Убейте Гаммериту и верните Катуму Удильщику из деревни Сломанного Клыка его лучшую блесну.\n\nКатум сказал, что Гаммерита ползает по берегу среди остальных черепах.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Лучшая блесна Катума x 1",
    [7817] = "|Cffffd100Требования:|r\n - Магическая ткань x 60",
    [7828] = "Убейте 5 среброгривых бродяг и 5 среброгривых ревунов по поручению охотника Мархора из деревни Сломанного Клыка во Внутренних землях.\n\nМархор сказал, что волки обитают в лесах Внутренних земель.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Среброгривый бродяга x 5\n - Среброгривый ревун x 5",
    [7829] = "Убейте 10 диких совухов по поручению охотника Мархора из деревни Сломанного Клыка во Внутренних землях.\n\nМархор сказал, что совухи обитают в лесах Внутренних земель.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Дикий совух x 10",
    [7830] = "Убейте остроклюва-повелителя небес и принесите его перо охотнику Мархору в деревню Сломанного Клыка.\n\nМархор говорил, что остроклювы водятся во Внутренних землях.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Перо остроклюва-повелителя небес x 1",
    [7839] = "Найдите потерянные инструменты кузнеца Углежога из деревни Сломанного Клыка во Внутренних землях.\n\nУглежог сказал, что инструменты могут быть в одном из храмов троллей из племени Порочной Ветви в северо-восточной части Внутренних земель. Также следует проверить на Осклизлой скале.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Потерянные инструменты Углежога x 1",
    [7840] = "Найдите обед Лярда из деревни Сломанного Клыка во Внутренних землях.\n\nЛярд сказал, что потерял свой обед на острове к северу, где могут встретиться тролли из племени Порочной Ветви.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Обед Лярда x 1",
    [7841] = "Убейте 8 гонцов, 5 разведчиков, 5 стрелков и 2 следопытов Высокодолья по поручению Отхо Можи'ко из деревни Сломанного Клыка.\n\nВысших эльфов из Высокодолья можно найти в Сторожке Кель'Данил в северо-западной части Внутренних земель.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Разведчик высокодолья x 8\n - Гонец высокодолья x 5\n - Следопыт высокодолья x 5\n - Стрелок высокодолья x 2",
    [7842] = "Принесите длинное элегантное перо Отхо Можи'ко из деревни Сломанного Клыка во Внутренних землях. Перо нужно собрать с местных грифонов, которых можно найти практически в любом месте Внутренних земель.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Длинное элегантное перо x 1",
    [7843] = "Доберитесь до Заоблачного Пика в западной части Внутренних земель и оставьте возле колодца в центре города последнее предупреждение для клана Громового Молота. После выполнения задания возвращайтесь к Отхо Можи'ко в деревню Сломанного Клыка.\n\nБерегитесь дворфов из клана Громового Молота и других сторонников Альянса.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Послание Громовому Молоту доставлено",
    [7844] = "Убейте 6 охотников за скальпами и 2 ворожеев из племени Порочной Ветви по поручению Яо'джин из деревни Сломанного Клыка.\n\nЯо'джин сказала, что эти тролли живут близ храмов Шаол'вата и Агол'вата в северо-восточной части Внутренних земель.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Охотник за скальпами из племени Порочной Ветви x 6\n - Ворожей из племени Порочной Ветви x 2",
    [8151] = "Поговорите с Огтинком из Азшары.",
    [8153] = "Принесите пару великолепных рогов мохоногого оленя Огтинку в Азшару. Огтинк обитает среди скал в северо-востоку от руин Эльдарата.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Великолепный рог бегового оленя x 2",
    [8231] = "Принесите 6 чешуй волношлепа Огтинку в Азшару. Огтинк обитает среди скал в северо-востоку от руин Эльдарата.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Чешуя волношлепа x 6",
    [8250] = "Найдите Саната Лим-Йо и добейтесь, чтобы он доставил вас к верховному магу Ксилему.",
    [8251] = "Принесите 10 горстей сияющей пыли верховному магу Ксилему.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Сияющая пыль x 10",
    [8252] = "Соберите 6 заколдованных кораллов и принесите их верховному магу Ксилему.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Заколдованный коралл x 6",
    [8254] = "Отыщи Огтинка в Азшаре.",
    [8255] = "Добудьте 4 железы здорового мохоногого оленя и принесите их Огтинку в Азшару. Огтинк обитает среди скал в северо-востоку от руин Эльдарата.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Железа здорового бегового оленя x 4",
    [8256] = "Принесите лимфу нежити Огтинку в Азшару. Огтинк обитает среди скал в северо-востоку от руин Эльдарата.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Лимфа нежити x 1",
    [8275] = "Поговорите с призывательницей ветров Гордый Рог в крепости Кенария.",
    [8276] = "Поговорите с призывательницей ветров Гордый Рог в крепости Кенария.",
    [8277] = "Принесите Битиссе Шаткобрус из крепости Кенария 8 жал камнехвостых скорпидов и 8 жвал песчаных пауков-быстроногов.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Жало камнехвостого скорпида x 8\n - Клык песчаного паука-быстронога x 8",
    [8278] = "Принесите Битиссе Шаткобрус из крепости Кенария 3 жала камнехвостых клешневиков, 3 жала камнехвостых живодеров и 3 клыка скальных пауков-скакунов.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Жало камнехвостового живодера x 3\n - Жало камнехвостого клешневика x 3\n - Клык скального паука-скакуна x 3",
    [8279] = "Принесите три главы Сумеречного словаря отшельнику Ортеллу из Силитуса.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Сумеречный словарь - Глава 1 x 1\n - Сумеречный словарь - Глава 2 x 1\n - Сумеречный словарь - Глава 3 x 1",
    [8280] = "Убейте 15 землечерпов по поручению призывательницы ветров Гордый Рог.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Землечерп x 15",
    [8281] = "Убейте 20 земляных сокрушителей по поручению призывательницы ветров Гордый Рог.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Земляной сокрушитель x 20",
    [8282] = "Принесите сумку Ноггля из гор в южной части Силитуса и верните ее хозяину в крепость Кенария.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Сумка Ноггла x 1",
    [8283] = "Принесите хелицеры Смертехвата Вишу Коцусу, капитану стражи.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Хелицеры смертехвата x 1",
    [8284] = "Геолог Ехидная из крепости Кенария просит вас собрать 8 фрагментов сумеречной таблички.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Фрагмент сумеречной таблички x 8",
    [8285] = "Отнесите восстановленную табличку отшельнику Ортеллу в Силитус.",
    [8287] = "Отнесите переписанный с таблички расшифрованный текст командору Мар'алиту.",
    [8304] = "Допросите обитателей лагеря Бронзоборода по поручению командора Мар'алита из крепости Кенария. Этот лагерь находится южнее крепости Кенария.\n\nСобрав необходимые сведения, вернитесь к командору Мар'алиту.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Франкал допрошен x 1\n - Рутгар допрошен x 1",
    [8308] = "Отнесите письмо Бранна Бронзоборода в лагерь Бронзоборода в южной части Силитуса.",
    [8318] = "Принесите 10 зашифрованных сумеречных текстов Бору Буйногривому в крепость Кенария.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Зашифрованный Сумеречный текст x 10",
    [8320] = "Убейте 10 геолордов из культа Сумеречного Молота и вернитесь к Хууму Буйногривому в крепость Кенария.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Сумеречный геолорд x 10",
    [8323] = "Принесите 10 зашифрованных Сумеречных текстов отшельнику Ортеллу.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Зашифрованный Сумеречный текст x 10",
    [8361] = "Убейте храмовника бездны по поручению Бора Буйногривого из крепости Кенария, что в Силитусе, и принесите в доказательство талисман бездны.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Талисман Бездны x 1",
    [8365] = "Добудьте 20 шляп пиратов Южных Морей и отнесите их Отти Кутюри в Порт Картеля в Танарисе.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Шляпа пирата Южных морей x 20",
    [8366] = "Начальник отдела безопасности Чепухастер из Порта Картеля в Танарисе просит убить 10 пиратов, 10 флибустьеров, 10 рабочих и 10 сорвиголов Южных морей.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Пират из братства Южных Морей x 10\n - Флибустьер из братства Южных Морей x 10\n - Портовый рабочий из братства Южных Морей x 10\n - Сорвиголова из братства Южных Морей x 10",
    [8410] = "Соберите образцы воздуха, огня, земли и воды по поручению Бат'ры Стража Ветра.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Первородный воздух x 1\n - Первородный огонь x 1\n - Первородная земля x 1\n - Первородная вода x 1",
    [8412] = "Принесите части тотема Духа Бат'ре Стражу Ветра.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Воспаленный паучий глаз x 8\n - Толстый черный коготь x 8",
    [8413] = "Принесите вудуистские перья Бат'ре Стражу Ветра.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Янтарное перо вуду x 2\n - Синее вудуистское перо x 2\n - Зеленое вудуистское перо x 2",
    [8414] = "Принесите 20 камней Плети приспешников верховному жрецу Тель'данису в Западные Чумные земли.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Камень приспешника Плети x 20",
    [8415] = "Обратитесь к командиру Ашламу Отважному Кулаку в Лагерь Промозглого Ветра.",
    [8416] = "Отнесите бездействующий камень командиру Ашламу Неутомимому на Берег Промозглого Ветра.",
    [8417] = "Поговорите с павшим героем Орды у входа в Выжженные земли.",
    [8418] = "Добудьте янтарные, синие и зеленые вудуистские перья у троллей из Затонувшего храма.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Янтарное перо вуду x 2\n - Синее вудуистское перо x 2\n - Зеленое вудуистское перо x 2",
    [8419] = "Принесите отрез ткани Скверны Бесенку в Оскверненный лес.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Ткань Скверны x 1",
    [8421] = "Принесите 10 кусков гнилой древесины и 4 порции экстракта Отравленной Крови Бесенку в Оскверненный лес.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Гнилая деревяшка x 10\n - Экстракт Отравленной Крови x 4",
    [8423] = "Убейте 7 зловепрей в Выжженных землях и вернитесь к павшему герою Орды.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Зловепрь x 7",
    [8424] = "Убейте приверженцев тени в Выжженных землях и вернитесь к павшему герою Орды.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Адепт культа Приверженцев Тени x 20\n - Сектант Культа Приверженцев Тени x 10\n - Лиходей культа Приверженцев Тени x 20",
    [8460] = "Гразл желает, чтобы вы заслужили доверие. Вам нужно убить 6 воинов, 6 землепроходцев и 6 садовников из племени Мертвого Леса. Выполнив поручение, возвращайтесь в южную часть Оскверненного леса к Изумрудному святилищу.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Воин из племени Мертвого Леса x 6\n - Землепроходец из племени Мертвого Леса x 6\n - Садовник из племени Мертвого Леса x 6",
    [8461] = "Нафиэн просит вас убить 6 дозорных, 6 мстителей и 6 шаманов из племени Мертвого Леса. По выполнении возвращайтесь на север Оскверненного леса ко входу в крепость Древобрюхов.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Дозорный из племени Мертвого Леса x 6\n - Мститель из племени Мертвого Леса x 6\n - Шаман из племени Мертвого Леса x 6",
    [8462] = "Отправляйтесь на север по главной дороге в Оскверненном лесу и поговорите с фурболгом по имени Нафиэн. Он стоит на страже у входа в крепость Древобрюхов.",
    [8464] = "Убейте 8 шаманов, 8 дозорных и 8 ведмедей из племени Зимней Спячки по просьбе Сальфы. Он ждет у входа в Крепость Древобрюхов в Зимних Ключах.\n|Cffffd100 |r\n|Cffffd100Требования:|r\n - Шаман из племени Зимней Спячки x 8\n - Дозорный из племени Зимней Спячки x 8\n - Ведмедь из племени Зимней Спячки x 8",
    [8465] = "Пройдите через Крепость Древобрюхов и выйдите к Зимним Ключам. Поговорите с Сальфой, который стоит на страже у входа в Крепость Древобрюхов.",
