    [784] = "殺死10個庫爾提拉斯水手和8個庫爾提拉斯水兵，再把本尼迪克上尉幹掉，然後回剃刀嶺向加索克復命。\n|Cffffd100 |r\n|Cffffd100需要：|r\n - 庫爾提拉斯水手 x 10\n - 庫爾提拉斯水兵 x 8\n - 本尼迪克上尉 x 1",
    [786] = "在森金村外面的拉爾·獵齒要求你粉碎科卡爾峭壁內的三份進攻計畫。\n|Cffffd100 |r\n|Cffffd100需要：|r\n - 試煉谷進攻計畫已經被摧毀 x 1\n - 森金村進攻計畫已經被摧毀 x 1\n - 奧格瑪進攻計畫已經被摧毀 x 1",
    [788] = "殺掉10隻雜斑野豬，然後向大獸穴裡的高內克報告。\n|Cffffd100 |r\n|Cffffd100需要：|r\n - 雜斑野豬 x 10",
    [789] = "帶10根工蠍的尾巴給大獸穴中的高內克。\n|Cffffd100 |r\n|Cffffd100需要：|r\n - 工蠍的尾巴 x 10",
    [790] = "殺死薩科斯並將它的爪子帶回去交給哈納祖。\n|Cffffd100 |r\n|Cffffd100需要：|r\n - 薩科斯的爪子 x 1",
    [791] = "剃刀嶺瞭望塔中的弗恩·凝眉想要8塊帆布腳料。\n|Cffffd100 |r\n|Cffffd100需要：|r\n - 帆布腳料 x 8",
    [792] = "殺死12隻邪靈劣魔，然後向大獸穴外的祖雷薩覆命。\n|Cffffd100 |r\n|Cffffd100需要：|r\n - 邪靈劣魔 x 12",
    [794] = "給大獸穴外的祖雷薩帶回火刃獎章。\n|Cffffd100 |r\n|Cffffd100需要：|r\n - 火刃獎章 x 1",
    [804] = "將哈納祖目前的狀況告訴大獸穴內的高內克。",
    [805] = "與森金村的加德林大師談話。",
    [806] = "把費索的爪子帶給剃刀嶺的奧戈尼爾·魂痕。\n|Cffffd100 |r\n|Cffffd100需要：|r\n - 費索的爪子 x 1",
    [808] = "到回音群島上去，將島上法陣中的明希納的顱骨帶回來，交給森金村的加德林大師。\n|Cffffd100 |r\n|Cffffd100需要：|r\n - 明希納的顱骨 x 1",
    [809] = "到貧瘠之地去和雅克塞羅斯談話。\n|Cffffd100 |r\n|Cffffd100需要：|r\n - 摧毀惡魔之種",
    [815] = "收集3個鞭尾龍的蛋，並把它們帶給剃刀嶺的廚師托爾卡。\n|Cffffd100 |r\n|Cffffd100需要：|r\n - 鞭尾龍的蛋 x 3",
    [816] = "把關於克魯恩的消息告訴剃刀嶺西北部農場中的米莎·托克倫。\n|Cffffd100 |r\n|Cffffd100需要：|r\n - 克魯恩的護身符 x 1",
    [817] = "收集四張杜洛塔虎皮，並把它們交給森金村的維爾林·長牙。\n|Cffffd100 |r\n|Cffffd100需要：|r\n - 杜洛塔虎皮 x 4",
    [818] = "把4個龍蝦人的眼睛和8小瓶蟹膠帶到森金村，交給沃納爾大師。\n|Cffffd100 |r\n|Cffffd100需要：|r\n - 龍蝦人的眼球 x 4\n - 蟹膠 x 8",
    [819] = "找到對老陳的空酒桶有所瞭解的人。",
    [821] = "收集5顆草原獅的獠牙、5個平原陸行鳥的腎臟和1根雷霆蜥蜴的角，把它們交給棘齒城的釀酒師德羅恩。\n|Cffffd100 |r\n|Cffffd100需要：|r\n - 草原獅的獠牙 x 5\n - 平原陸行鳥的腎臟 x 5\n - 雷霆蜥蜴的角 x 1",
    [822] = "收集5個閃電腺、1個雷鷹的唾液腺和1個科多獸的肝臟，把它們交給棘齒城裡的釀酒師德羅恩。\n|Cffffd100 |r\n|Cffffd100需要：|r\n - 閃電腺 x 5\n - 雷鷹的唾液腺 x 1\n - 科多獸的肝臟 x 1",
    [823] = "和剃刀嶺的奧戈尼爾·魂痕談話。",
    [824] = "將被污染的水球交給梣谷左拉姆加前哨站的耶努薩克雷。",
    [825] = "剃刀嶺的加索克要你從海岸邊的艦隊殘骸中打撈出3件地精的工具。\n|Cffffd100 |r\n|Cffffd100需要：|r\n - 地精的工具 x 3",
    [826] = "打敗札拉贊恩。\n\n殺死8個巫毒食人妖和8個妖術食人妖。\n\n把札拉贊恩的頭顱帶給加德林大師。\n|Cffffd100 |r\n|Cffffd100需要：|r\n - 妖術食人妖 x 8\n - 巫毒食人妖 x 8\n - 札拉贊恩的頭顱 x 1",
    [827] = "在骷髏石中的火刃祭司身上收集灼熱項圈，\n\n然後交給瑪高茲。\n|Cffffd100 |r\n|Cffffd100需要：|r\n - 灼熱項圈 x 6",
    [828] = "和瑪高茲談話。",
    [829] = "給奧格瑪的尼爾魯·火刃帶去一個項圈樣本。",
    [830] = "把普勞德摩爾將軍的命令帶給剃刀嶺的加索克。",
    [831] = "把普勞德摩爾將軍的命令交給奧格瑪城中的納茲格雷爾，他就在索爾的大廳裡。",
    [832] = "把這顆燃影之眼帶給尼爾魯·火刃。",
